const userId = $json.query?.user_id ?? $json.body?.user_id;

if (typeof userId !== 'string' || userId.trim() === '') {
  throw new Error('Parâmetro user_id é obrigatório (?user_id=).');
}

return [{ json: { user_id: userId.trim() } }];

const row = $input.first()?.json || {};
const dias = Array.isArray(row.dias) ? row.dias : [];

const parsedDias = dias.map((dia) => {
  const totalXp = Number(dia.totalXp ?? 0) || 0;
  const metaDia = Number(dia.metaDia ?? 0) || 0;
  const status = metaDia > 0 && totalXp >= metaDia
    ? 'concluido'
    : totalXp > 0
      ? 'parcial'
      : 'pendente';

  return {
    data: dia.data ?? null,
    label: dia.label ?? null,
    totalXp,
    xpBase: Number(dia.xpBase ?? 0) || 0,
    xpBonus: Number(dia.xpBonus ?? 0) || 0,
    xpConversa: Number(dia.xpConversa ?? 0) || 0,
    xpQuests: Number(dia.xpQuests ?? 0) || 0,
    metaDia,
    metaConversa: Number(dia.metaConversa ?? 0) || 0,
    metaQuests: Number(dia.metaQuests ?? 0) || 0,
    status
  };
});

const xpSemanaTotal = Number(row.xp_semana_total ?? 0) || 0;
const xpMetaSemana = Number(row.meta_semana_total ?? 0) || undefined;
const percentualMeta = xpMetaSemana && xpMetaSemana > 0
  ? Math.min(100, Math.round((xpSemanaTotal / xpMetaSemana) * 100))
  : undefined;

const diasConcluidos = parsedDias.filter((dia) => dia.metaDia > 0 && dia.totalXp >= dia.metaDia).length;
const streakDias = Number(row.streak_dias ?? 0) || 0;

const summary = {
  usuarioId: row.usuario_id ?? null,
  semanaInicio: row.semana_inicio ?? null,
  semanaFim: row.semana_fim ?? null,
  streakAtualDias: streakDias,
  xpSemanaTotal,
  xpMetaSemana,
  dias: parsedDias
};

return [{
  json: {
    success: true,
    usuario_id: summary.usuarioId,
    card_weekly_progress: {
      ...summary,
      percentualMeta,
      diasConcluidos
    }
  }
}];
