const input = $input.first()?.json ?? {};
const query = input.query ?? {};
const body = input.body ?? {};

const candidate = [
  query.usuario_id,
  query.user_id,
  query.id_usuario,
  body.usuario_id,
  body.user_id,
  body.id_usuario,
  input.headers?.usuario_id,
  input.headers?.user_id,
].find((value) => typeof value === 'string' && value.trim().length > 0);

if (!candidate) {
  throw new Error('usuario_id obrigatório');
}

return [{
  json: {
    usuario_id: candidate.trim(),
  }
}];

const entrada = $items('Validar Entrada', 0, 0)?.json ?? {};
const estadoRow = $items('Buscar Estado', 0, 0)?.json ?? null;
const questItems = $input.all();

const parseNumber = (value, fallback = null) => {
  if (value === null || value === undefined) return fallback;
  const num = Number(value);
  return Number.isFinite(num) ? num : fallback;
};

const parseString = (value) => {
  if (value === null || value === undefined) return null;
  return typeof value === 'string' ? value : String(value);
};

const sequenciaStatus = (() => {
  if (!estadoRow || estadoRow.sequencia_status == null) return null;
  if (typeof estadoRow.sequencia_status === 'object') return estadoRow.sequencia_status;
  try {
    return JSON.parse(estadoRow.sequencia_status);
  } catch (error) {
    return null;
  }
})();

const quests = questItems.map((item) => {
  const row = item.json;
  const config = row.config && typeof row.config === 'object'
    ? row.config
    : (() => {
        if (!row.config) return null;
        try {
          return JSON.parse(row.config);
        } catch (error) {
          return null;
        }
      })();

  return {
    instancia_id: parseString(row.instancia_id),
    meta_codigo: parseString(row.meta_codigo) ?? '',
    status: parseString(row.status) ?? 'pendente',
    titulo: parseString(row.titulo) ?? 'Quest personalizada',
    descricao: parseString(row.descricao),
    contexto_origem: parseString(row.contexto_origem),
    progresso_meta: parseNumber(row.progresso_meta, 1) ?? 1,
    progresso_atual: parseNumber(row.progresso_atual, 0) ?? 0,
    concluido_em: parseString(row.concluido_em),
    config,
    xp_recompensa: parseNumber(row.xp_recompensa, null),
    prioridade: config && typeof config.prioridade === 'string' ? config.prioridade : null,
    recorrencia: config && typeof config.recorrencia === 'string' ? config.recorrencia : null,
  };
});

const snapshot = {
  usuario_id: parseString(estadoRow?.usuario_id) ?? parseString(entrada.usuario_id) ?? '',
  xp_total: parseNumber(estadoRow?.xp_total, 0) ?? 0,
  xp_proximo_nivel: parseNumber(estadoRow?.xp_proximo_nivel, null),
  nivel_atual: parseNumber(estadoRow?.nivel_atual, 1) ?? 1,
  titulo_nivel: parseString(estadoRow?.titulo_nivel),
  sequencia_atual: parseNumber(estadoRow?.sequencia_atual, 0) ?? 0,
  sequencia_recorde: parseNumber(estadoRow?.sequencia_recorde, 0) ?? 0,
  meta_sequencia_codigo: parseString(estadoRow?.meta_sequencia_codigo),
  proxima_meta_codigo: parseString(estadoRow?.proxima_meta_codigo),
  sequencia_status: sequenciaStatus,
  quests_personalizadas: quests,
};

return [{ json: snapshot }];
