// organiza_json
// ====================================================
// NODE: organiza_json
// Separa a mensagem do usuário e o JSON de contexto (se existir)
// ====================================================

// Resposta do agente
const aiResponse = $input.first()?.json?.output ?? "";

// Dados de controle da interação
const jsNode = $('intertion_count').first().json;
const config = $('mindquest_config').first().json;
const sessionLimit = Number(config?.limit_iteration) || 8;
const interactionCount = jsNode?.interaction_count ?? 1;
const isLastInteraction = interactionCount >= sessionLimit;

// Se não for a última interação → retorna só a mensagem
if (interactionCount < sessionLimit) {
  return {
    user_message: aiResponse,
    context_json: null,
    has_context: false,
    interaction_count: interactionCount,
    is_last_interaction: false,
    session_limit: sessionLimit,
    original_response: aiResponse
  };
}

// ----------------------------------------------------
// Tenta detectar JSON dentro da resposta (se existir)
// ----------------------------------------------------
const jsonStartPattern = /\{[\s\n]*"interaction_completa"/;
const jsonMatch = aiResponse.match(jsonStartPattern);

let userMessage = aiResponse;
let contextJson = null;
let hasJson = false;

if (jsonMatch) {
  const jsonStartIndex = jsonMatch.index;

  // Mensagem de texto (antes do JSON)
  userMessage = aiResponse.substring(0, jsonStartIndex).trim();

  // JSON (a partir do padrão encontrado)
  const jsonString = aiResponse.substring(jsonStartIndex).trim();

  try {
    contextJson = JSON.parse(jsonString);
    hasJson = true;
  } catch (err) {
    console.error('Erro ao fazer parse do JSON:', err);
  }
}

// ----------------------------------------------------
// Retorna objeto estruturado para uso posterior
// ----------------------------------------------------
return {
  user_message: userMessage,
  context_json: contextJson,
  has_context: hasJson,
  original_response: aiResponse,
  interaction_count: interactionCount,
  is_last_interaction: isLastInteraction,
  session_limit: sessionLimit
};

// ====================================================
// N8N CODE NODE: junta_dados_gravacao (VERSÃO FINAL)
// ====================================================

const dadosOrganizados = $('organiza_json').first().json;
const chatMemory = $input.item.json;
const config = $('mindquest_config').first().json;
const mensagensCompletas = Array.isArray(chatMemory.messages) ? chatMemory.messages : [];

// Determina limite de sessão (fallback seguro)
const configuredLimit = Number(config?.limit_iteration);
const sessionLimit = Number.isFinite(configuredLimit) && configuredLimit > 0
  ? configuredLimit
  : Number(dadosOrganizados.total_interactions) || 8;

// Corrigir session_id (remover template strings)
const startData = $('start').first().json;
const transcricaoData = $('Transcrição').first().json;
const sessionId = `${startData["body.instance"]}${startData.usr_id}${transcricaoData.WhstsApp_Number}`;

const USER_REGEX = /\*\*Mensagem do Usu[aá]rio:\*\*\s*([\s\S]*?)(?=\n\*\*|\n<|$)/i;

function extrairMensagemUsuario(promptCompleto) {
  if (!promptCompleto || typeof promptCompleto !== 'string') {
    return '';
  }

  const match = promptCompleto.match(USER_REGEX);
  if (match && match[1]) {
    const cleaned = match[1].replace(/^[:\-–\s]+/, '').trim();
    if (cleaned) {
      return cleaned;
    }
  }

  const linhas = promptCompleto
    .split(/\r?\n/)
    .map(l => l.trim())
    .filter(l => l && !l.startsWith('#') && !l.startsWith('=') && !l.startsWith('**') && !l.startsWith('-') && !l.startsWith('_') && !l.startsWith('<'));

  if (linhas.length > 0) {
    return linhas[0];
  }

  return promptCompleto.trim();
}

function textoValido(valor) {
  return typeof valor === 'string' && valor.trim().length > 0;
}

const mensagens = [];
let interactionNumber = 1;

for (const msg of mensagensCompletas) {
  if (!msg || typeof msg !== 'object') {
    continue;
  }

  const role = msg.role ?? null;
  const systemFlag = msg.system === true || role === 'system';

  const humanTexto = textoValido(msg.human)
    ? msg.human.trim()
    : ((role === 'user' || role === 'human') && textoValido(msg.content) ? msg.content.trim() : null);

  const aiTexto = textoValido(msg.ai)
    ? msg.ai.trim()
    : ((role === 'assistant' || role === 'ai') && textoValido(msg.content) ? msg.content.trim() : null);

  const isSystemOnly = systemFlag && !humanTexto && !aiTexto;
  if (isSystemOnly) {
    continue;
  }

  const isControleInterno = humanTexto && humanTexto.toLowerCase().startsWith('sistema:');
  if (systemFlag || isControleInterno) {
    if (!aiTexto) {
      continue;
    }
  }

  if (humanTexto && !isControleInterno) {
    const textoExtraido = extrairMensagemUsuario(humanTexto);
    if (textoValido(textoExtraido)) {
      mensagens.push({
        interaction: interactionNumber,
        autor: 'usuario',
        texto: textoExtraido,
        timestamp: new Date().toISOString()
      });
    }
  }

  if (aiTexto) {
    mensagens.push({
      interaction: interactionNumber,
      autor: 'agente',
      texto: aiTexto,
      timestamp: new Date().toISOString()
    });
    interactionNumber++;
  }
}

const contextoFinal = {
  situacao_emocional: dadosOrganizados.situacao_emocional || '',
  contextos_mencionados: dadosOrganizados.contextos_mencionados || [],
  sentimentos_expressos: dadosOrganizados.sentimentos_expressos || [],
  eventos_importantes: dadosOrganizados.eventos_importantes || [],
  intensidade_geral: dadosOrganizados.intensidade_geral || 'média',
  validado_pelo_usuario: dadosOrganizados.validado_pelo_usuario || false,
  observacoes_agente: dadosOrganizados.observacoes_agente || ''
};

return {
  whatsapp_numero: transcricaoData.WhstsApp_Number,
  usuario_id: startData.usr_id,
  session_id: sessionId,
  total_interactions: dadosOrganizados.total_interactions || sessionLimit,
  status: dadosOrganizados.status || 'completa',
  mensagens: JSON.stringify(mensagens),
  contexto_final: JSON.stringify(contextoFinal),
  humor_autoavaliado: dadosOrganizados.humor_autoavaliado || null,
  emocao_primaria: dadosOrganizados.emocao_primaria || null,
  intensidade_emocao: dadosOrganizados.intensidade_emocao || null,
  energia_detectada: dadosOrganizados.energia_detectada || null,
  qualidade_interacao: dadosOrganizados.qualidade_interacao || null,
  emoji_dia: dadosOrganizados.emoji_dia || null,
  contexto_validado: dadosOrganizados.validado_pelo_usuario || false,
  pronta_para_especialista: dadosOrganizados.pronta_para_especialista || false,
  observacoes_usuario: dadosOrganizados.observacoes_agente || ''
};

// Lê valor do GET (sua versão retorna em propertyName; deixo compatível)
const raw = $('Redis GET Lock').first().json.propertyName ?? $('Redis GET Lock').first().json.value ?? null;

const now = Math.floor(Date.now() / 1000);
const unlockAt = Number(raw);

// Se o valor não for numérico, não há como calcular
if (!Number.isFinite(unlockAt) || unlockAt <= 0) {
  return { remaining_seconds: 0, remaining_hm: "0h 0m", unlock_clock: "00:00" };
}

let remaining = Math.max(unlockAt - now, 0);
const hours = Math.floor(remaining / 3600);
const minutes = Math.floor((remaining % 3600) / 60);

// Hora exata (Europe/Lisbon)
const unlockDate = new Date(unlockAt * 1000);
const hm = new Intl.DateTimeFormat('pt-PT', { hour: '2-digit', minute: '2-digit', hour12: false, timeZone: 'Europe/Lisbon' }).format(unlockDate);

return {
  remaining_seconds: remaining,
  remaining_hm: `${hours}h ${minutes}m`,
  unlock_clock: hm
};

// ============================================
// CONTROLE DE INTERAÇÕES - N8N CODE NODE
// Simplificado para retorno do contador
// ============================================

// Recupera memória agregada para este usuário
let memoryData = null;
try {
  const items = $input.all();
  if (items?.length) {
    memoryData = items[0]?.json ?? null;
  }
} catch {
  memoryData = null;
}

// Calcula a interação atual
let interactionCount = 1;
const messages = memoryData?.messages;
if (Array.isArray(messages)) {
  const systemMessages = messages.filter(msg => msg?.system);
  interactionCount = systemMessages.length + 1;
}

// Retorna somente o contador (mantendo paired item)
return [{
  json: {
    interaction_count: interactionCount
  },
  pairedItem: 0
}];

