// ==========================================
// C√ÅLCULO DE XP E ATUALIZA√á√ÉO DE GAMIFICA√á√ÉO
// ==========================================

// Pega o estado do node "Buscar Estado Atual"
const estado = $('Buscar Estado Atual').first().json;

// === SANITIZAR DADOS DO RESUMO ===
const resumo = $('start').first().json;

console.log('=== DEBUG REFLEX√ÉO ===');
console.log('resumo completo:', JSON.stringify(resumo, null, 2));
console.log('tem_reflexao original:', resumo.tem_reflexao);
console.log('tipo:', typeof resumo.tem_reflexao);
console.log('total_palavras_usuario:', resumo.total_palavras_usuario);

// Converter tem_reflexao para boolean verdadeiro
const tem_reflexao = resumo.tem_reflexao === true || 
                     resumo.tem_reflexao === 'true' || 
                     resumo.tem_reflexao === ':true';

console.log('tem_reflexao convertido:', tem_reflexao);

// Converter total_palavras para n√∫mero
const total_palavras = parseInt(resumo.total_palavras_usuario) || 0;

console.log('total_palavras convertido:', total_palavras);

// Data de hoje
const data_hoje = new Date($('start').item.json.data_conversa).toISOString().split('T')[0];

// === 1. CALCULAR TIPO DE CONVERSA ===
let xp_base = 20; // conversa simples

if (tem_reflexao && total_palavras > 50) {
  xp_base = 40; // conversa profunda
  console.log('‚úÖ CONVERSA PROFUNDA - XP Base: 40');
} else if (tem_reflexao) {
  xp_base = 30; // conversa com reflex√£o
  console.log('‚úÖ CONVERSA COM REFLEX√ÉO - XP Base: 30');
} else {
  console.log('‚ùå CONVERSA SIMPLES - XP Base: 20');
}

// === 2. CALCULAR B√îNUS DE STREAK (USA dias_consecutivos DA QUERY) ===
const dias_consecutivos = parseInt(estado.dias_consecutivos) || 0;
let bonus_streak = 0;

if (dias_consecutivos >= 30) {
  bonus_streak = 20;
} else if (dias_consecutivos >= 7) {
  bonus_streak = 10;
}

console.log('üî• Dias consecutivos (da query):', dias_consecutivos);
console.log('üíé B√¥nus streak:', bonus_streak);

// === 3. ATUALIZAR QUEST DI√ÅRIA ===
const quest_data = estado.quest_diaria_data;
let quest_status = 'pendente';
let quest_progresso = 0;
let bonus_quest = 0;

console.log('Quest data anterior:', quest_data, '| Data hoje:', data_hoje);

// Se √© um novo dia, reseta quest
if (quest_data !== data_hoje) {
  quest_status = 'pendente';
  quest_progresso = 0;
  console.log('üéØ Novo dia - Quest resetada');
}

// Atualiza progresso da quest
if (tem_reflexao) {
  quest_status = 'completa';
  quest_progresso = 100;
  bonus_quest = 30;
  console.log('‚úÖ Quest COMPLETA - B√¥nus: +30 XP');
} else {
  quest_status = 'parcial';
  quest_progresso = 50;
  console.log('‚è≥ Quest PARCIAL - Sem b√¥nus');
}

// === 4. CALCULAR XP TOTAL ===
const xp_ganho = xp_base + bonus_streak + bonus_quest;
const novo_xp_total = (estado.xp_total || 0) + xp_ganho;

console.log('üíé XP Ganho:', xp_ganho, '(base:', xp_base, '+ streak:', bonus_streak, '+ quest:', bonus_quest, ')');
console.log('üíé XP Total:', novo_xp_total);

// === 5. CALCULAR NOVO N√çVEL ===
let nivel_atual = estado.nivel_atual || 1;
let xp_proximo_nivel = estado.xp_proximo_nivel || 200;
let titulo_nivel = estado.titulo_nivel || 'Explorador';

const niveis = {
  1: { titulo: 'Explorador', xp: 200 },
  2: { titulo: 'Aprendiz', xp: 550 },
  3: { titulo: 'Observador', xp: 1050 },
  4: { titulo: 'Focado', xp: 1700 },
  5: { titulo: 'Praticante', xp: 2500 },
  6: { titulo: 'Consciente', xp: 3450 },
  7: { titulo: 'Iluminado', xp: 4550 },
  8: { titulo: 'S√°bio', xp: 5800 },
  9: { titulo: 'Ascendente', xp: 7200 },
  10: { titulo: 'Mestre', xp: 9000 },
  15: { titulo: 'Transcendente', xp: 15000 }
};

// === 6. VERIFICAR CONQUISTAS ===
const conquistas_atuais = estado.conquistas_desbloqueadas || [];
const novas_conquistas = [];

const total_conversas = (estado.total_conversas || 0) + 1;
const total_reflexoes = (estado.total_reflexoes || 0) + (tem_reflexao ? 1 : 0);

console.log('üìä Total conversas:', total_conversas);
console.log('üìä Total reflex√µes:', total_reflexoes, '(tem_reflexao:', tem_reflexao, ')');
console.log('üìä Dias consecutivos:', dias_consecutivos);

// Conquistas a verificar
const conquistas_map = {
  'primeira_conversa': {
    criterio: total_conversas === 1,
    nome: 'Primeira Semente',
    emoji: 'üå±',
    xp_bonus: 50,
    categoria: 'primeiros_passos'
  },
  'primeira_semana': {
    criterio: total_conversas === 7,
    nome: 'Semana Completa',
    emoji: 'üéØ',
    xp_bonus: 100,
    categoria: 'primeiros_passos'
  },
  'streak_7_dias': {
    criterio: dias_consecutivos === 7,  // ‚Üê USA dias_consecutivos
    nome: 'Chama Acesa',
    emoji: 'üî•',
    xp_bonus: 150,
    categoria: 'consistencia'
  },
  'streak_30_dias': {
    criterio: dias_consecutivos === 30,  // ‚Üê USA dias_consecutivos
    nome: 'Consist√™ncia Bronze',
    emoji: 'ü•â',
    xp_bonus: 300,
    categoria: 'consistencia'
  },
  'primeira_reflexao': {
    criterio: total_reflexoes === 1,
    nome: 'Pensador',
    emoji: 'üí≠',
    xp_bonus: 50,
    categoria: 'primeiros_passos'
  },
  'reflexao_profunda': {
    criterio: total_reflexoes === 10,
    nome: 'Reflex√£o Profunda',
    emoji: 'üß†',
    xp_bonus: 200,
    categoria: 'profundidade'
  }
};

// Verifica conquistas n√£o desbloqueadas
let xp_bonus_conquistas = 0;
console.log('üèÜ Verificando conquistas...');

for (const [id, conquista] of Object.entries(conquistas_map)) {
  const ja_desbloqueada = conquistas_atuais.some(c => c.id === id);

  console.log(`  - ${id}: crit√©rio=${conquista.criterio}, j√° desbloqueada=${ja_desbloqueada}`);

  if (!ja_desbloqueada && conquista.criterio) {
    novas_conquistas.push({
      id: id,
      nome: conquista.nome,
      emoji: conquista.emoji,
      xp_bonus: conquista.xp_bonus,
      categoria: conquista.categoria,
      desbloqueada_em: new Date().toISOString()
    });
    xp_bonus_conquistas += conquista.xp_bonus;
    console.log(`  ‚úÖ DESBLOQUEADA: ${conquista.emoji} ${conquista.nome} (+${conquista.xp_bonus} XP)`);
  }
}

console.log('üèÜ Total conquistas desbloqueadas:', novas_conquistas.length);
console.log('üèÜ XP b√¥nus de conquistas:', xp_bonus_conquistas);

// Adiciona XP de conquistas ao total
const xp_total_final = novo_xp_total + xp_bonus_conquistas;

console.log('üíé XP TOTAL FINAL:', xp_total_final);

// Recalcula n√≠vel com XP final
while (xp_total_final >= xp_proximo_nivel && nivel_atual < 15) {
  nivel_atual++;
  if (niveis[nivel_atual]) {
    xp_proximo_nivel = niveis[nivel_atual].xp;
    titulo_nivel = niveis[nivel_atual].titulo;
  } else {
    xp_proximo_nivel = (nivel_atual * 150) + 50;
    titulo_nivel = `N√≠vel ${nivel_atual}`;
  }
  console.log('‚¨ÜÔ∏è SUBIU DE N√çVEL!', nivel_atual, '-', titulo_nivel);
}

// === 7. MELHOR STREAK (RECORDE) ===
const melhor_streak = Math.max(estado.melhor_streak || 0, dias_consecutivos);

// === 8. GERAR NOVA QUEST DI√ÅRIA ===
const quests_possiveis = [
  'Complete sua conversa di√°ria e fa√ßa uma reflex√£o profunda sobre seu dia',
  'Compartilhe como voc√™ est√° se sentindo hoje e o que te trouxe energia',
  'Reflita sobre uma pequena vit√≥ria de hoje, por menor que pare√ßa',
  'Identifique uma emo√ß√£o que sentiu hoje e explore o que a causou'
];

const nova_quest = quests_possiveis[Math.floor(Math.random() * quests_possiveis.length)];

console.log('üéØ Nova quest gerada:', nova_quest);

// === RESUMO FINAL ===
console.log('=== RESUMO FINAL ===');
console.log('XP Total:', xp_total_final);
console.log('N√≠vel:', nivel_atual, '-', titulo_nivel);
console.log('Dias Consecutivos:', dias_consecutivos);
console.log('Quest:', quest_status, '-', quest_progresso + '%');
console.log('Reflex√µes:', total_reflexoes);
console.log('Conversas:', total_conversas);

// === RETORNO ===
return [{
  json: {
    usuario_id: $('start').item.json.usuario_id,
    xp_total: xp_total_final,
    nivel_atual: nivel_atual,
    xp_proximo_nivel: xp_proximo_nivel,
    titulo_nivel: titulo_nivel,
    streak_conversas_dias: dias_consecutivos,  // ‚Üê ATUALIZADO COM VALOR CORRETO
    melhor_streak: melhor_streak,
    ultima_conversa_data: data_hoje,
    quest_diaria_status: quest_status,
    quest_diaria_progresso: quest_progresso,
    quest_diaria_descricao: nova_quest,
    quest_diaria_data: data_hoje,
    total_conversas: total_conversas,
    total_reflexoes: total_reflexoes,
    conquistas_json: JSON.stringify(novas_conquistas),
    total_xp_ganho_hoje: xp_ganho + xp_bonus_conquistas,
    ultima_conquista_id: novas_conquistas.length > 0 ? novas_conquistas[novas_conquistas.length - 1].id : null,
    ultima_conquista_data: novas_conquistas.length > 0 ? new Date().toISOString() : null,

    _debug: {
      xp_base: xp_base,
      bonus_streak: bonus_streak,
      bonus_quest: bonus_quest,
      xp_bonus_conquistas: xp_bonus_conquistas,
      xp_ganho_total: xp_ganho + xp_bonus_conquistas,
      subiu_nivel: nivel_atual > (estado.nivel_atual || 1),
      conquistasDesbloqueadas: novas_conquistas.length,
      tem_reflexao_usado: tem_reflexao,
      total_palavras_usado: total_palavras,
      dias_consecutivos_usado: dias_consecutivos
    }
  }
}];
