// ==========================================
// Cﾃ´CULO DE XP E ATUALIZAﾃﾃグ DE GAMIFICAﾃﾃグ
// ==========================================

const getNumber = (value, fallback = 0) => {
  const num = Number(value);
  return Number.isFinite(num) ? num : fallback;
};

const toNumberOrNull = (value) => {
  if (value === null || value === undefined || value === '') {
    return null;
  }
  const num = Number(value);
  return Number.isFinite(num) ? num : null;
};

const parseJson = (value) => {
  if (!value) {
    return null;
  }
  if (typeof value === 'object') {
    return value;
  }
  try {
    return JSON.parse(String(value));
  } catch (error) {
    return null;
  }
};

const estado = ($('Buscar Estado Atual').first()?.json) ?? {};
const questsRaw = $('Buscar Quests').all().map((item) => item.json ?? {});
const resumo = ($('start').first()?.json) ?? {};

const temReflexao = resumo.tem_reflexao === true || resumo.tem_reflexao === 'true' || resumo.tem_reflexao === ':true';
const totalPalavras = getNumber(resumo.total_palavras_usuario, 0);

const dataConversaRaw = resumo.data_conversa ?? estado.ultima_conversa_data ?? new Date().toISOString();
const dataHoje = new Date(dataConversaRaw).toISOString().split('T')[0];

const diasConsecutivos = getNumber(estado.dias_consecutivos, 0);

// === 1. Cﾃ´CULO DE XP BASE ===
let xpBase = 20;
if (temReflexao && totalPalavras > 50) {
  xpBase = 40;
} else if (temReflexao) {
  xpBase = 30;
}

// === 2. Bﾃ年US DE STREAK ===
let bonusStreak = 0;
if (diasConsecutivos >= 30) {
  bonusStreak = 20;
} else if (diasConsecutivos >= 7) {
  bonusStreak = 10;
}

// === 3. PREPARAR DADOS DOS QUESTS ===
const nowIso = new Date().toISOString();
const questStates = questsRaw.map((quest) => {
  const dadosExtras = parseJson(quest.dados_extras) ?? null;
  const metaRaw = quest.progresso_meta ?? quest.gatilho_valor ?? (quest.tipo === 'diaria' ? 1 : null);
  const meta = metaRaw !== null ? toNumberOrNull(metaRaw) : null;
  const referenciaData = quest.referencia_data ? String(quest.referencia_data).split('T')[0] : null;

  return {
    id: quest.id,
    quest_id: quest.quest_id,
    codigo: quest.codigo,
    titulo: quest.titulo,
    descricao: quest.descricao,
    categoria: quest.categoria,
    tipo: quest.tipo,
    gatilho_tipo: quest.gatilho_tipo,
    gatilho_valor: quest.gatilho_valor,
    xp_recompensa: getNumber(quest.xp_recompensa, 0),
    repeticao: quest.repeticao,
    sequencia: getNumber(quest.sequencia, 0),
    referencia_data: referenciaData,
    dados_extras: dadosExtras,
    meta: meta,
    original: {
      status: quest.status,
      progresso_atual: getNumber(quest.progresso_atual, 0),
      progresso_percentual: getNumber(quest.progresso_percentual, 0),
      xp_concedido: getNumber(quest.xp_concedido, 0),
      concluido_em: quest.concluido_em ?? null
    },
    final: {
      status: quest.status,
      progresso_atual: getNumber(quest.progresso_atual, 0),
      progresso_percentual: getNumber(quest.progresso_percentual, 0),
      xp_concedido: getNumber(quest.xp_concedido, 0),
      concluido_em: quest.concluido_em ?? null,
      dados_extras: dadosExtras
    }
  };
});

const totalConversasAntes = getNumber(estado.total_conversas, 0);
const totalReflexoesAntes = getNumber(estado.total_reflexoes, 0);

const progressValues = {
  total_conversas: totalConversasAntes + 1,
  dias_consecutivos: diasConsecutivos,
  total_reflexoes: totalReflexoesAntes + (temReflexao ? 1 : 0),
  diario: temReflexao ? 1 : 0
};

let xpBonusQuests = 0;
const novasConquistas = [];

for (const quest of questStates) {
  const meta = quest.meta;

  if (quest.gatilho_tipo && Object.prototype.hasOwnProperty.call(progressValues, quest.gatilho_tipo)) {
    const candidate = progressValues[quest.gatilho_tipo];
    if (quest.gatilho_tipo === 'diario') {
      const dailyMeta = meta && meta > 0 ? meta : 1;
      quest.final.progresso_atual = temReflexao ? dailyMeta : 0;
    } else if (candidate > quest.final.progresso_atual) {
      quest.final.progresso_atual = candidate;
    }
  }

  if (quest.gatilho_tipo === 'diario') {
    quest.final.progresso_percentual = temReflexao ? 100 : 0;
  } else if (meta && meta > 0) {
    const percentual = (quest.final.progresso_atual / meta) * 100;
    quest.final.progresso_percentual = Math.min(100, Math.round(percentual));
  } else {
    quest.final.progresso_percentual = Math.min(100, quest.final.progresso_percentual);
  }

  let shouldComplete = false;
  if (quest.original.status !== 'completa') {
    if (quest.gatilho_tipo === 'diario') {
      shouldComplete = temReflexao;
    } else if (meta && meta > 0 && quest.final.progresso_atual >= meta) {
      shouldComplete = true;
    }
  }

  if (shouldComplete) {
    quest.final.status = 'completa';
    quest.final.progresso_atual = meta ?? quest.final.progresso_atual;
    quest.final.progresso_percentual = 100;
    quest.final.concluido_em = nowIso;
    if (quest.final.xp_concedido < quest.xp_recompensa) {
      const xpToAdd = quest.xp_recompensa - quest.final.xp_concedido;
      xpBonusQuests += xpToAdd;
      quest.final.xp_concedido = quest.xp_recompensa;
      novasConquistas.push({
        usuario_quest_id: quest.id,
        codigo: quest.codigo,
        titulo: quest.titulo,
        categoria: quest.categoria,
        xp_bonus: quest.xp_recompensa,
        concluido_em: nowIso
      });
    }
  } else if (quest.final.status === 'pendente' && (quest.final.progresso_atual > 0 || quest.final.progresso_percentual > 0)) {
    quest.final.status = 'ativa';
  }
}

const systemQuests = questStates
  .filter((quest) => quest.tipo === 'sistema')
  .sort((a, b) => a.sequencia - b.sequencia);

for (const quest of systemQuests) {
  if (quest.final.status !== 'completa') {
    const allPreviousComplete = systemQuests
      .filter((other) => other.sequencia < quest.sequencia)
      .every((other) => other.final.status === 'completa');
    if (allPreviousComplete && quest.final.status === 'pendente') {
      quest.final.status = 'ativa';
    }
    break;
  }
}

const questUpdatesPayload = questStates
  .filter((quest) =>
    quest.final.status !== quest.original.status ||
    quest.final.progresso_atual !== quest.original.progresso_atual ||
    quest.final.progresso_percentual !== quest.original.progresso_percentual ||
    quest.final.xp_concedido !== quest.original.xp_concedido ||
    (quest.final.concluido_em ?? null) !== (quest.original.concluido_em ?? null)
  )
  .map((quest) => ({
    quest_record_id: quest.id,
    status: quest.final.status,
    progresso_atual: quest.final.progresso_atual,
    progresso_percentual: quest.final.progresso_percentual,
    xp_concedido: quest.final.xp_concedido,
    concluido_em: quest.final.concluido_em,
    dados_extras: quest.final.dados_extras
  }));

const totalConversasQuest = questStates
  .filter((quest) => quest.codigo === 'primeira_conversa' || quest.gatilho_tipo === 'total_conversas')
  .reduce((max, quest) => Math.max(max, quest.final.progresso_atual), 0);
const totalReflexoesQuest = questStates
  .filter((quest) => quest.codigo === 'primeira_reflexao' || quest.gatilho_tipo === 'total_reflexoes')
  .reduce((max, quest) => Math.max(max, quest.final.progresso_atual), 0);
const totalConversas = Math.max(progressValues.total_conversas, totalConversasQuest);
const totalReflexoes = Math.max(progressValues.total_reflexoes, totalReflexoesQuest);

const dailyQuestHoje = questStates.find((quest) => quest.tipo === 'diaria' && quest.referencia_data === dataHoje);
let questDiariaStatus = 'pendente';
let questDiariaProgresso = 0;
let questDiariaDescricao = null;
let questDiariaId = null;
if (dailyQuestHoje) {
  questDiariaDescricao = dailyQuestHoje.descricao || dailyQuestHoje.titulo || null;
  questDiariaProgresso = dailyQuestHoje.final.progresso_percentual;
  questDiariaId = dailyQuestHoje.id;
  if (dailyQuestHoje.final.progresso_percentual >= 100 || dailyQuestHoje.final.status === 'completa') {
    questDiariaStatus = 'completa';
  } else if (dailyQuestHoje.final.progresso_percentual > 0) {
    questDiariaStatus = 'parcial';
  }
}

const totalXpQuestsAntes = questStates.reduce((sum, quest) => sum + getNumber(quest.original.xp_concedido, 0), 0);
const totalXpQuestsDepois = questStates.reduce((sum, quest) => sum + getNumber(quest.final.xp_concedido, 0), 0);
const xpTotalAnterior = getNumber(estado.xp_total, 0);
const xpBaseHistorico = Math.max(0, xpTotalAnterior - totalXpQuestsAntes);
const xpQuestsGanho = totalXpQuestsDepois - totalXpQuestsAntes;
const totalXpGanhoHoje = xpBase + bonusStreak + Math.max(0, xpQuestsGanho);
const xpTotalFinal = xpTotalAnterior + totalXpGanhoHoje;

let nivelAtual = getNumber(estado.nivel_atual, 1);
let xpProximoNivel = getNumber(estado.xp_proximo_nivel, 200);
let tituloNivel = estado.titulo_nivel || 'Explorador';

const niveis = {
  1: { titulo: 'Explorador', xp: 200 },
  2: { titulo: 'Aprendiz', xp: 550 },
  3: { titulo: 'Observador', xp: 1050 },
  4: { titulo: 'Focado', xp: 1700 },
  5: { titulo: 'Praticante', xp: 2500 },
  6: { titulo: 'Consciente', xp: 3450 },
  7: { titulo: 'Iluminado', xp: 4550 },
  8: { titulo: 'Sﾃ｡bio', xp: 5800 },
  9: { titulo: 'Ascendente', xp: 7200 },
  10: { titulo: 'Mestre', xp: 9000 },
  15: { titulo: 'Transcendente', xp: 15000 }
};

let nivelTemp = nivelAtual;
let xpThreshold = xpProximoNivel;
while (xpTotalFinal >= xpThreshold && nivelTemp < 15) {
  nivelTemp += 1;
  if (niveis[nivelTemp]) {
    xpThreshold = niveis[nivelTemp].xp;
    tituloNivel = niveis[nivelTemp].titulo;
  } else {
    xpThreshold = (nivelTemp * 150) + 50;
    tituloNivel = `Nﾃｭvel ${nivelTemp}`;
  }
}
if (nivelTemp !== nivelAtual) {
  nivelAtual = nivelTemp;
  xpProximoNivel = xpThreshold;
}

const melhorStreak = Math.max(getNumber(estado.melhor_streak, 0), diasConsecutivos);
const ultimaConquistaId = novasConquistas.length > 0 ? novasConquistas[novasConquistas.length - 1].usuario_quest_id : (estado.ultima_conquista_id ?? null);
const ultimaConquistaData = novasConquistas.length > 0 ? novasConquistas[novasConquistas.length - 1].concluido_em : (estado.ultima_conquista_data ?? null);

const questsSnapshot = questStates.map((quest) => ({
  usuario_quest_id: quest.id,
  quest_id: quest.quest_id,
  codigo: quest.codigo,
  titulo: quest.titulo,
  descricao: quest.descricao,
  categoria: quest.categoria,
  tipo: quest.tipo,
  status: quest.final.status,
  progresso_atual: quest.final.progresso_atual,
  progresso_meta: quest.meta,
  progresso_percentual: quest.final.progresso_percentual,
  xp_concedido: quest.final.xp_concedido,
  xp_recompensa: quest.xp_recompensa,
  referencia_data: quest.referencia_data,
  concluido_em: quest.final.concluido_em
}));

const questsPorCategoria = questsSnapshot.reduce((acc, quest) => {
  if (!acc[quest.categoria]) {
    acc[quest.categoria] = [];
  }
  acc[quest.categoria].push(quest);
  return acc;
}, {});

return [{
  json: {
    usuario_id: estado.usuario_id ?? $('start').first().json.usuario_id,
    xp_total: xpTotalFinal,
    nivel_atual: nivelAtual,
    xp_proximo_nivel: xpProximoNivel,
    titulo_nivel: tituloNivel,
    streak_conversas_dias: diasConsecutivos,
    melhor_streak: melhorStreak,
    ultima_conversa_data: dataHoje,
    quest_diaria_status: questDiariaStatus,
    quest_diaria_progresso: questDiariaProgresso,
    quest_diaria_descricao: questDiariaDescricao,
    quest_diaria_data: dataHoje,
    quest_diaria_id: questDiariaId,
    total_conversas: totalConversas,
    total_reflexoes: totalReflexoes,
    total_xp_ganho_hoje: totalXpGanhoHoje,
    ultima_conquista_id: ultimaConquistaId,
    ultima_conquista_data: ultimaConquistaData,
    dias_consecutivos: diasConsecutivos,
    xp_base: xpBase,
    bonus_streak: bonusStreak,
    xp_bonus_quests: xpBonusQuests,
    quest_updates: JSON.stringify(questUpdatesPayload),
    quests_snapshot: questsSnapshot,
    quests_por_categoria: questsPorCategoria,
    novas_conquistas: novasConquistas,
    tem_reflexao: temReflexao,
    total_palavras: totalPalavras,
    _debug: {
      totalConversasAntes,
      totalConversas,
      totalReflexoesAntes,
      totalReflexoes,
      diasConsecutivos,
      xpBase,
      bonusStreak,
      xpBonusQuests,
      totalXpQuestsAntes,
      totalXpQuestsDepois,
      xpBaseHistorico,
      xpQuestsGanho,
      totalXpGanhoHoje,
      quests_processadas: questStates.length,
      novasConquistas,
      questsSnapshot
    }
  }
}];
