SELECT json_build_object(
  'sabotadores_catalogo',
  COALESCE(json_agg(
    json_build_object(
      'id', sc.id,
      'nome', sc.nome,
      'emoji', sc.emoji,
      'descricao', sc.descricao
    )
    ORDER BY sc.nome
  ), '[]'::json)
) AS sabotadores_catalogo
FROM public.sabotadores_catalogo sc;

