const getJson = (nodeName) => {
  const data = $items(nodeName, 0, 0) || [];
  const item = data[0] || {};
  return item.json || {};
};

const entrada = getJson("Normalizar Entrada");
const questsRaw = getJson("Buscar Quests Ativas");
const conversasRaw = getJson("Buscar Conversas Recentes");
const insightsRaw = getJson("Buscar Insights Recentes");
const sabotadoresRaw = getJson("Buscar Sabotadores Recentes");
const areasRaw = getJson("Listar Áreas da Vida");
const sabotadoresCatRaw = getJson("Listar Sabotadores Catálogo");

const quests = questsRaw.contexto || {};
const conversas = conversasRaw.conversas || {};
const insights = insightsRaw.insights || {};
const sabotadores = sabotadoresRaw.sabotadores || {};
const areasVida = areasRaw.areas_vida || [];
const sabotadoresCatalogo = sabotadoresCatRaw.sabotadores_catalogo || [];

return [{
  json: {
    usuario_id: entrada.usuario_id,
    chat_id: entrada.chat_id,
    quests_ativas: quests.quests_ativas || [],
    conversas_recentes: conversas.conversas_recentes || [],
    insights_recentes: insights.insights_recentes || [],
    sabotadores_recentes: sabotadores.sabotadores_recentes || [],
    areas_vida_disponiveis: areasVida,
    sabotadores_catalogo: sabotadoresCatalogo
  }
}];

const getJson = (nodeName) => {
  const data = $items(nodeName, 0, 0) || [];
  const item = data[0] || {};
  return item.json || {};
};

const contexto = getJson("Montar Contexto");
const resultado = $json.output || { novas_quests: [], atualizacoes: [] };

return [{
  json: {
    usuario_id: contexto.usuario_id,
    chat_id: contexto.chat_id,
    quests_ativas: contexto.quests_ativas,
    novas_quests_sugeridas: Array.isArray(resultado.novas_quests) ? resultado.novas_quests : [],
    atualizacoes_sugeridas: Array.isArray(resultado.atualizacoes) ? resultado.atualizacoes : [],
    sabotadores_catalogo: contexto.sabotadores_catalogo || []
  }
}];

const entrada = $input.item.json || {};
const existentes = Array.isArray(entrada.quests_ativas) ? entrada.quests_ativas : [];
const limite = 4;
const dedupe = new Set();

const normalizeUuid = (value) => {
  if (typeof value !== 'string') return null;
  const trimmed = value.trim();
  if (!trimmed) return null;
  const uuidRegex = /^[0-9a-fA-F]{8}-[0-9a-fA-F]{4}-[0-9a-fA-F]{4}-[0-9a-fA-F]{4}-[0-9a-fA-F]{12}$/;
  return uuidRegex.test(trimmed) ? trimmed : null;
};

const clampComplexidade = (value) => {
  const num = Number(value);
  if (!Number.isFinite(num)) return 0;
  return Math.min(5, Math.max(0, Math.round(num)));
};

const sabotadoresCatalogoIds = new Set(
  Array.isArray(entrada.sabotadores_catalogo)
    ? entrada.sabotadores_catalogo
        .map((item) => normalizeUuid(item && item.id))
        .filter(Boolean)
    : []
);

for (const ativo of existentes) {
  const key = `${(ativo.contexto_origem || '').toLowerCase()}|${(ativo.titulo || '').toLowerCase()}`;
  dedupe.add(key);
}

const novas = [];
for (const quest of entrada.novas_quests_sugeridas || []) {
  if (!quest || typeof quest !== 'object') continue;
  const key = `${(quest.contexto_origem || '').toLowerCase()}|${(quest.titulo || '').toLowerCase()}`;
  if (dedupe.has(key)) continue;
  if (existentes.length + novas.length >= limite) break;

  const areaVidaId = normalizeUuid(quest.area_vida_id);
  const insightId = normalizeUuid(quest.insight_id);
  if (!areaVidaId || !insightId) continue;

  const rawSabId = normalizeUuid(quest.sabotador_id);
  const sabotadorId = rawSabId && sabotadoresCatalogoIds.has(rawSabId) ? rawSabId : null;

  dedupe.add(key);
  novas.push({
    ...quest,
    prioridade: quest.prioridade || 'media',
    recorrencia: quest.recorrencia || 'unica',
    status_inicial: quest.status_inicial || 'pendente',
    progresso_meta: quest.progresso_meta ?? 1,
    xp_recompensa: quest.xp_recompensa ?? 0,
    insight_id: insightId,
    sabotador_id: sabotadorId,
    area_vida_id: areaVidaId,
    complexidade: clampComplexidade(quest.complexidade)
  });
}

const atualizacoes = [];
for (const upd of entrada.atualizacoes_sugeridas || []) {
  if (!upd || typeof upd !== 'object') continue;
  const novoStatus = upd.novo_status || '';
  if (!['concluida','vencida','cancelada','reiniciada'].includes(novoStatus)) continue;
  atualizacoes.push({
    meta_codigo: upd.meta_codigo || null,
    instancia_id: upd.instancia_id || null,
    novo_status: novoStatus,
    progresso_atual: upd.progresso_atual ?? 0,
    xp_extra: upd.xp_extra ?? 0
  });
}

return [{
  json: {
    usuario_id: entrada.usuario_id,
    novas_quests: novas,
    atualizacoes: atualizacoes
  }
}];

const data = $items("Aplicar Limites & Dedupe", 0, 0) || [];
const entrada = (data[0] || {}).json || {};
const retorno = $input.item.json || {};

return [{
  json: {
    usuario_id: entrada.usuario_id,
    quests_registradas: entrada.novas_quests,
    atualizacoes_enviadas: entrada.atualizacoes,
    gamificacao_retorno: retorno
  }
}];
