// VALIDAÇÃO INTELIGENTE DO DELTA + JANELA DE TEMPO
const item = $input.first().json;
const mensagens = item.mensagens || [];
const ultimaProcessada = item.ultima_processada || 0;
const totalAtual = item.total_interactions || 0;
const ultimaProcessadaEm = item.ultima_processada_em;
const mensagensNovas = Array.isArray(mensagens) ? mensagens.slice(ultimaProcessada) : [];
const mensagensUsuario = mensagensNovas.filter(m => m.autor && m.autor.toLowerCase() === 'usuario');
const palavrasNovas = mensagensUsuario.map(m => (m.texto || '').split(/\s+/).filter(p => p.length > 0).length).reduce((a, b) => a + b, 0);
const numInteracoesNovas = totalAtual - ultimaProcessada;
const JANELA_MAXIMA_HORAS = 2;
let horasSemProcessar = null;
let tempoExcedido = false;
if (ultimaProcessadaEm) {
  const agora = new Date();
  const ultimaData = new Date(ultimaProcessadaEm);
  horasSemProcessar = (agora - ultimaData) / (1000 * 60 * 60);
  tempoExcedido = horasSemProcessar > JANELA_MAXIMA_HORAS;
}
const volumeAlto = palavrasNovas >= 80;
const volumeMedio = palavrasNovas >= 30 && palavrasNovas < 80;
const volumeBaixo = palavrasNovas < 30;
const mensagensTexto = mensagensUsuario.map(m => m.texto || '').join(' ').toLowerCase();
const temPalavrasChave = /\b(sinto|sentir|sentindo|senti|emoção|emocional|medo|frustração|ansiedade|ansioso|triste|feliz|preocupado|reflexão|pensei|pensando|percebi|percebo)\b/i.test(mensagensTexto);
const conversaProfunda = mensagensUsuario.length >= 4;
let deveProcessar = false;
let motivo = '';
if (tempoExcedido) {
  deveProcessar = true;
  motivo = `Tempo excedido: ${horasSemProcessar.toFixed(1)}h sem processar (máx: ${JANELA_MAXIMA_HORAS}h) - ${palavrasNovas} palavras acumuladas`;
} else if (volumeAlto) {
  deveProcessar = true;
  motivo = `Volume alto: ${palavrasNovas} palavras novas`;
} else if (volumeMedio && (temPalavrasChave || conversaProfunda)) {
  deveProcessar = true;
  motivo = `Volume médio com contexto relevante: ${palavrasNovas} palavras`;
} else if (volumeBaixo) {
  deveProcessar = false;
  const tempoInfo = horasSemProcessar ? ` (último processamento: ${horasSemProcessar.toFixed(1)}h atrás)` : '';
  motivo = `Volume insuficiente: ${palavrasNovas} palavras (mín: 30)${tempoInfo}`;
} else {
  deveProcessar = false;
  motivo = `Volume médio sem contexto significativo: ${palavrasNovas} palavras`;
}
const mensagensNovasTexto = mensagensNovas.map(m => `${m.autor}: ${m.texto}`).join('\n');
return [{...item, deve_processar: deveProcessar, motivo_validacao: motivo, palavras_novas: palavrasNovas, interacoes_novas: numInteracoesNovas, horas_sem_processar: horasSemProcessar, tempo_excedido: tempoExcedido, mensagens_delta: mensagensNovasTexto, mensagens_completas: item.mensagens}];

// PREPARA DADOS PARA OS EXPERTS (APENAS DELTA)
const item = $input.first().json;
const mensagensDelta = item.mensagens_delta || '';
return [{usuario_id: item.usuario_id, chat_id: item.id, data_conversa: item.data_conversa, mensagens: mensagensDelta, usr_nome_preferencia: item.usr_nome_preferencia || 'Usuário', total_interactions: item.total_interactions, palavras_novas: item.palavras_novas, interacoes_novas: item.interacoes_novas, motivo_validacao: item.motivo_validacao}];
