// Verificar se estamos no período correto
const horaAtual = new Date().getHours();
let periodoAtual = 'manha';

if (horaAtual >= 6 && horaAtual < 12) {
  periodoAtual = 'manha';
} else if (horaAtual >= 12 && horaAtual < 18) {
  periodoAtual = 'tarde';
} else if (horaAtual >= 18 || horaAtual < 6) {
  periodoAtual = 'noite';
}

// Filtrar apenas usuários com período correspondente
const items = $input.all();
const filtered = items.filter(item => {
  const periodoUsuario = item.json.lembretes_periodo || 'manha';
  return periodoUsuario === periodoAtual;
});

return filtered.map(item => ({
  json: {
    ...item.json,
    periodo_atual: periodoAtual
  }
}));

// Preparar notificações baseadas nas pendências encontradas
// Suporte a múltiplos canais: push e whatsapp

const items = $input.all();
const notificacoes = [];

// Agrupar por usuario_id
const porUsuario = {};

for (const item of items) {
  const usuarioId = item.json.usuario_id;
  if (!usuarioId) continue;

  if (!porUsuario[usuarioId]) {
    porUsuario[usuarioId] = {
      usuario_id: usuarioId,
      tokens: [],
      whatsapp_numero: null,
      lembretes_canais: [],
      lembretes_conversas_diarias: false,
      lembretes_quests: false,
      conversas: { sem_conversa_hoje: false, conversa_pendente: false },
      quests: []
    };
  }

  // Token push
  if (item.json.token) {
    if (!porUsuario[usuarioId].tokens.includes(item.json.token)) {
      porUsuario[usuarioId].tokens.push(item.json.token);
    }
  }

  // WhatsApp
  if (item.json.whatsapp_numero) {
    porUsuario[usuarioId].whatsapp_numero = item.json.whatsapp_numero;
  }

  // Canais
  if (item.json.lembretes_canais) {
    porUsuario[usuarioId].lembretes_canais = item.json.lembretes_canais;
  }

  // Configurações
  if (item.json.lembretes_conversas_diarias !== undefined) {
    porUsuario[usuarioId].lembretes_conversas_diarias = item.json.lembretes_conversas_diarias;
  }
  if (item.json.lembretes_quests !== undefined) {
    porUsuario[usuarioId].lembretes_quests = item.json.lembretes_quests;
  }

  // Conversas pendentes
  if (item.json.tipo_pendencia === 'sem_conversa_hoje') {
    porUsuario[usuarioId].conversas.sem_conversa_hoje = true;
  } else if (item.json.tipo_pendencia === 'conversa_pendente') {
    porUsuario[usuarioId].conversas.conversa_pendente = true;
  }

  // Quests pendentes
  if (item.json.quest_titulo) {
    const questJaExiste = porUsuario[usuarioId].quests.some(
      q => q.titulo === item.json.quest_titulo
    );
    if (!questJaExiste) {
      porUsuario[usuarioId].quests.push({
        titulo: item.json.quest_titulo,
        data_planejada: item.json.data_planejada
      });
    }
  }
}

// Criar notificações por canal
for (const usuarioId in porUsuario) {
  const dados = porUsuario[usuarioId];
  const mensagens = [];

  // Conversas (só se habilitado)
  if (dados.lembretes_conversas_diarias) {
    if (dados.conversas.sem_conversa_hoje) {
      mensagens.push('Você ainda não fez sua conversa diária hoje');
    }
    if (dados.conversas.conversa_pendente) {
      mensagens.push('Você tem uma conversa não terminada');
    }
  }

  // Quests (só se habilitado)
  if (dados.lembretes_quests && dados.quests.length > 0) {
    mensagens.push(`Você tem ${dados.quests.length} quest${dados.quests.length > 1 ? 's' : ''} pendente${dados.quests.length > 1 ? 's' : ''} hoje`);
  }

  // Só criar notificação se houver pendências
  if (mensagens.length > 0) {
    const titulo = 'MindQuest - Lembretes';
    const corpo = mensagens.join('. ') + '.';

    // Push: uma notificação por dispositivo
    if (dados.lembretes_canais.includes('push') && dados.tokens.length > 0) {
      for (const token of dados.tokens) {
        notificacoes.push({
          json: {
            usuario_id: dados.usuario_id,
            canal: 'push',
            token: token,
            titulo: titulo,
            corpo: corpo,
            tipo: 'lembrete'
          }
        });
      }
    }

    // WhatsApp: uma notificação por usuário
    if (dados.lembretes_canais.includes('whatsapp') && dados.whatsapp_numero) {
      notificacoes.push({
        json: {
          usuario_id: dados.usuario_id,
          canal: 'whatsapp',
          whatsapp_numero: dados.whatsapp_numero,
          titulo: titulo,
          corpo: corpo,
          tipo: 'lembrete'
        }
      });
    }
  }
}

return notificacoes;
