// v2.0.0: Selecionar Quest por Rotação de Categorias
// REGRAS:
// 1. Rotação: TCC → Estoicismo → Sabotador → Personalizada → Objetivo
// 2. Se não tem da categoria, pula para próxima
// 3. Se humor OU energia < 5, manda TCC/Estoicismo e pula a rotação

const CATEGORIAS_ROTACAO = ['tcc', 'estoicismo', 'sabotador', 'personalizada', 'objetivo'];
const CATEGORIAS_BEM_ESTAR = ['tcc', 'estoicismo'];

const items = $input.all();
const resultados = [];

for (const item of items) {
  const data = item.json;
  const humorNivel = data.humor_nivel || 6;
  const energiaNivel = data.energia_nivel || 6;
  const ultimaCategoria = data.ultima_categoria_notificada || null;
  const questsPorCategoria = data.quests_por_categoria || {};

  let questSelecionada = null;
  let categoriaSelecionada = null;
  let motivoSelecao = '';

  // REGRA 3: Se humor OU energia < 5, priorizar bem-estar
  const precisaBemEstar = humorNivel < 5 || energiaNivel < 5;

  if (precisaBemEstar) {
    // Buscar quest de TCC ou Estoicismo
    for (const cat of CATEGORIAS_BEM_ESTAR) {
      const questsDaCategoria = questsPorCategoria[cat] || [];
      if (Array.isArray(questsDaCategoria) && questsDaCategoria.length > 0) {
        questSelecionada = questsDaCategoria[0];
        categoriaSelecionada = cat;
        motivoSelecao = 'bem_estar_prioritario';
        break;
      }
    }
  }

  // Se não precisou de bem-estar OU não encontrou quest de bem-estar
  if (!questSelecionada) {
    // REGRA 1 e 2: Rotação de categorias
    let indiceInicio = 0;

    if (ultimaCategoria) {
      const indiceUltima = CATEGORIAS_ROTACAO.indexOf(ultimaCategoria);
      if (indiceUltima !== -1) {
        indiceInicio = (indiceUltima + 1) % CATEGORIAS_ROTACAO.length;
      }
    }

    // Tentar cada categoria a partir do índice de início
    for (let i = 0; i < CATEGORIAS_ROTACAO.length; i++) {
      const indice = (indiceInicio + i) % CATEGORIAS_ROTACAO.length;
      const cat = CATEGORIAS_ROTACAO[indice];
      const questsDaCategoria = questsPorCategoria[cat] || [];

      if (Array.isArray(questsDaCategoria) && questsDaCategoria.length > 0) {
        questSelecionada = questsDaCategoria[0];
        categoriaSelecionada = cat;
        motivoSelecao = 'rotacao_categoria';
        break;
      }
    }
  }

  // Se ainda não encontrou nenhuma quest
  if (!questSelecionada) {
    motivoSelecao = 'sem_quest_disponivel';
  }

  resultados.push({
    json: {
      ...data,
      quest_selecionada: questSelecionada,
      categoria_selecionada: categoriaSelecionada,
      motivo_selecao: motivoSelecao,
      precisa_bem_estar: precisaBemEstar
    }
  });
}

return resultados;

