// CONTEXTO COMPLETO v2 - Consolida dados + sessão do dia
const usuarioId = $('start').first().json.usr_id;
const config = $('config').first().json;
const dadosUsr = $('busca_dados_usr').first().json;
const contexto = $('busca_contexto').first().json;

// Verifica se é primeira conversa
const totalConversas = Number(contexto?.total_conversas || 0);
const isPrimeiraConversa = totalConversas === 0;

// Verifica se usuário tem objetivos
const objetivos = contexto?.objetivos_especificos || [];
const temObjetivos = Array.isArray(objetivos) && objetivos.length > 0;

// Sessão do dia (se existir, continua; senão, é nova)
const sessaoHojeId = contexto?.sessao_hoje_id || null;
const sessaoHojeMensagens = contexto?.sessao_hoje_mensagens || [];
const isNovaSessao = !sessaoHojeId;

// Conta interações da sessão atual
const mensagensArray = Array.isArray(sessaoHojeMensagens) ? sessaoHojeMensagens : [];
const interacaoAtual = mensagensArray.filter(m => m?.autor === 'usuario').length + 1;

// Tom de conversa (default: empatico)
const tomConversa = dadosUsr?.tom_conversa || 'empatico';

return [{
  usuario_id: usuarioId,
  nome_preferencia: dadosUsr?.nome_preferencia || null,
  nome_assistente: dadosUsr?.nome_assistente || 'Mind',
  whatsapp_numero: dadosUsr?.whatsapp_numero || null,
  faixa_etaria: dadosUsr?.faixa_etaria || null,
  sobre_voce: dadosUsr?.sobre_voce || null,
  tom_conversa: tomConversa,
  objetivos_especificos: objetivos,
  tem_objetivos: temObjetivos,
  objetivo_padrao: contexto?.objetivo_padrao_id || null,
  sabotador_mais_ativo: contexto?.sabotador_mais_ativo || null,
  perfil_bigfive_primario: contexto?.perfil_bigfive_primario || null,
  quests_ativas: contexto?.quests_ativas || [],
  ultimas_conversas: contexto?.ultimas_conversas || [],
  nivel_atual: contexto?.nivel_atual || 1,
  titulo_nivel: contexto?.titulo_nivel || 'Explorador',
  total_conversas: totalConversas,
  is_primeira_conversa: isPrimeiraConversa,
  sessao_hoje_id: sessaoHojeId,
  sessao_hoje_mensagens: sessaoHojeMensagens,
  is_nova_sessao: isNovaSessao,
  interacao_atual: interacaoAtual
}];

