// CONTEXTO COMPLETO v7 - Ignora objetivo padr√£o no flag tem_objetivos
const usuarioId = $('start').first().json.usr_id;
const config = $('config').first().json;
const dadosUsr = $('busca_dados_usr').first().json;
const contexto = $('busca_contexto').first().json;

// Notifica√ß√£o recente (se houver)
const notifResult = $('busca_notificacao').first()?.json;
const temNotificacao = notifResult && notifResult.id;
const notificacaoRecente = temNotificacao ? {
  id: notifResult.id,
  tipo: notifResult.tipo,
  titulo: notifResult.titulo || '',
  mensagem: notifResult.mensagem || '',
  contexto_mentor: notifResult.contexto_mentor || '',
  quests_mencionadas: notifResult.quests_mencionadas || [],
  referencia_id: notifResult.referencia_id,
  criado_em: notifResult.criado_em
} : null;

// Verifica se √© primeira conversa
const totalConversas = Number(contexto?.total_conversas || 0);
const isPrimeiraConversa = totalConversas === 0;

// Verifica se usu√°rio tem objetivos AL√âM do padr√£o
const objetivos = contexto?.objetivos_especificos || [];

// Filtra objetivos configur√°veis (ignora padr√£o)
const objetivosConfigurados = Array.isArray(objetivos) 
  ? objetivos.filter(obj => !obj.is_padrao)
  : [];

// tem_objetivos = true apenas se tem objetivos AL√âM do padr√£o
const temObjetivos = objetivosConfigurados.length > 0;

// Quests - apenas totais (detalhes via tool)
const questsAtivasTotal = Number(contexto?.quests_ativas_total || 0);
const questsDisponiveisTotal = Number(contexto?.quests_disponiveis_total || 0);
const temQuests = questsAtivasTotal > 0 || questsDisponiveisTotal > 0;

// Sess√£o do dia
const sessaoHojeId = contexto?.sessao_hoje_id || null;
const sessaoHojeStatus = contexto?.sessao_hoje_status || null;
const sessaoHojeMensagens = contexto?.sessao_hoje_mensagens || [];

// NOVO CICLO: n√£o existe sess√£o OU sess√£o est√° finalizada
const isNovoCiclo = !sessaoHojeId || sessaoHojeStatus === 'finalizado';

// Se √© novo ciclo (sess√£o finalizada), reseta contador de intera√ß√µes
const mensagensArray = Array.isArray(sessaoHojeMensagens) ? sessaoHojeMensagens : [];
const interacaoAtual = isNovoCiclo ? 1 : mensagensArray.filter(m => m?.autor === 'usuario').length + 1;

// Tom de conversa (default: empatico)
const tomConversa = dadosUsr?.tom_conversa || 'empatico';

return [{
  usuario_id: usuarioId,
  nome_preferencia: dadosUsr?.nome_preferencia || null,
  nome_assistente: dadosUsr?.nome_assistente || 'Mind',
  whatsapp_numero: dadosUsr?.whatsapp_numero || null,
  faixa_etaria: dadosUsr?.faixa_etaria || null,
  sobre_voce: dadosUsr?.sobre_voce || null,
  tom_conversa: tomConversa,
  // Objetivos - separados para clareza
  objetivos_especificos: objetivos,           // todos (incluindo padr√£o)
  objetivos_configurados: objetivosConfigurados, // apenas os configur√°veis
  tem_objetivos: temObjetivos,                // true s√≥ se tem configurados
  objetivo_padrao: contexto?.objetivo_padrao_id || null,
  sabotador_mais_ativo: contexto?.sabotador_mais_ativo || null,
  perfil_bigfive_primario: contexto?.perfil_bigfive_primario || null,
  // Quests - apenas indicadores
  tem_quests: temQuests,
  quests_ativas_total: questsAtivasTotal,
  quests_disponiveis_total: questsDisponiveisTotal,
  // Hist√≥rico
  ultimas_conversas: contexto?.ultimas_conversas || [],
  nivel_atual: contexto?.nivel_atual || 1,
  titulo_nivel: contexto?.titulo_nivel || 'Explorador',
  total_conversas: totalConversas,
  is_primeira_conversa: isPrimeiraConversa,
  sessao_hoje_id: sessaoHojeId,
  sessao_hoje_mensagens: sessaoHojeMensagens,
  // Novo ciclo quando sess√£o finalizada
  is_nova_sessao: isNovoCiclo,
  is_novo_ciclo: isNovoCiclo,
  sessao_anterior_finalizada: sessaoHojeStatus === 'finalizado',
  interacao_atual: interacaoAtual,
  // Notifica√ß√£o
  tem_notificacao_recente: temNotificacao,
  notificacao_recente: notificacaoRecente
}];

