// PROCESSA RESPOSTA DO MENTOR v7 - trigger sem limite de intera√ß√µes
const output = $input.first()?.json?.output ?? {};
const contexto = $('contexto_completo').first().json;
const transcricao = $('transcricao').first().json;

// Flags do mentor
const checkpointEncerramento = output.checkpoint_encerramento === true;
const temaFechado = output.tema_atual_fechado === true;
const objetivoSugerido = output.objetivo_sugerido || null;

// Tema atual
const temaAtual = output.tema_atual || { titulo: '', resumo: [], decisoes: [] };

// Trigger: apenas quando mentor sinaliza fechamento
const triggerExperts = temaFechado || checkpointEncerramento;

// Prepara mensagens para gravar (incremental)
const mensagemUsuario = {
  interaction: contexto.interacao_atual,
  autor: 'usuario',
  texto: transcricao.message || '',
  timestamp: new Date().toISOString()
};

const mensagemMentor = {
  interaction: contexto.interacao_atual,
  autor: 'agente',
  texto: output.mensagem_usuario || '',
  timestamp: new Date().toISOString(),
  tema: temaAtual.titulo || null
};

// Mensagens existentes + novas
const mensagensExistentes = Array.isArray(contexto.sessao_hoje_mensagens) 
  ? contexto.sessao_hoje_mensagens 
  : [];
const mensagensAtualizadas = [...mensagensExistentes, mensagemUsuario, mensagemMentor];

return [{
  user_message: output.mensagem_usuario || '',
  checkpoint_encerramento: checkpointEncerramento,
  tema_fechado: temaFechado,
  tema_atual: temaAtual,
  trigger_experts: triggerExperts,
  objetivo_sugerido: objetivoSugerido,
  interacao_atual: contexto.interacao_atual,
  sessao_id: contexto.sessao_hoje_id,
  is_nova_sessao: contexto.is_nova_sessao,
  mensagens_atualizadas: JSON.stringify(mensagensAtualizadas),
  total_interacoes: mensagensAtualizadas.filter(m => m.autor === 'usuario').length
}];

