const input = $input.item.json || {};

const usuarioId = input.usuario_id;
if (!usuarioId) {
  throw new Error('usuario_id obrigatório');
}

return [{
  json: {
    usuario_id: usuarioId,
    chat_id: input.chat_id || null
  }
}];

const getJson = (nodeName) => {
  const data = $items(nodeName, 0, 0) || [];
  const item = data[0] || {};
  return item.json || {};
};

const getNested = (source, path, fallback = undefined) => {
  return path.reduce((acc, key) => (acc && acc[key] !== undefined ? acc[key] : undefined), source) ?? fallback;
};

const entrada = getJson("Normalizar Entrada");
const verificacaoRaw = getJson("Buscar Quests e Verificar Limite");
const questsRaw = verificacaoRaw;

const conversasRaw = getJson("Buscar Conversas Recentes");
const objetivosRaw = getJson("Buscar Objetivos Ativos");
const sabotadoresRaw = getJson("Buscar Sabotadores Ativo");
const areasRaw = getJson("Listar Áreas da Vida");
const estagioRaw = getJson("Buscar Estágio Usuário");
const catalogoRaw = getJson("Buscar Quests Catálogo");

const quests = questsRaw.contexto || {};
const questsAtivas = Array.isArray(quests.quests_ativas) ? quests.quests_ativas : [];

// === REPROCESSAMENTO (v1.4.1) ===
const isReprocessamento = verificacaoRaw.is_reprocessamento === true;
const questsSubstituiveis = Array.isArray(quests.quests_substituiveis) ? quests.quests_substituiveis : [];
const totalPalavrasUsuario = verificacaoRaw.total_palavras_usuario || 0;
const totalQuestsHoje = verificacaoRaw.total_quests_hoje || 0;
const sabotadoresHoje = verificacaoRaw.sabotadores_hoje || 0;
const personalizadasHoje = verificacaoRaw.personalizadas_hoje || 0;
const catalogoHoje = verificacaoRaw.catalogo_hoje || 0;

// Lista de quests já criadas (para evitar duplicatas)
const questsJaCriadas = questsAtivas.map(q => ({
  id: q.id,
  titulo: q.titulo || q.config?.titulo || '',
  catalogo_id: q.catalogo_id,
  contexto_origem: q.contexto_origem || '',
  status: q.status,
  concluido_em: q.concluido_em
}));

// IDs de catálogo já usados (para priorizar quests novas)
const catalogoIdsJaUsados = new Set(
  questsAtivas
    .filter(q => q.catalogo_id)
    .map(q => q.catalogo_id)
);

const estagioJornada = estagioRaw.estagio_jornada || 1;

const catalogoData = catalogoRaw.catalogo || {};
const questsCatalogo = Array.isArray(catalogoData.quests_catalogo) ? catalogoData.quests_catalogo : [];

// Filtrar quests do catálogo: priorizar as que ainda não foram usadas
// EXCLUIR quests de sabotador do catálogo (v1.3.18 - sabotadores são gerados do zero)
const questsCatalogoSemSabotador = questsCatalogo.filter(q => !q.sabotador_id);
const questsCatalogoNovas = questsCatalogoSemSabotador.filter(q => !catalogoIdsJaUsados.has(q.id));
const questsCatalogoUsadas = questsCatalogoSemSabotador.filter(q => catalogoIdsJaUsados.has(q.id));

// === CONVERSAS RECENTES ===
const conversas = conversasRaw.conversas || {};
const conversasRecentes = Array.isArray(conversas.conversas_recentes) ? conversas.conversas_recentes : [];

// === PEGAR chat_id DA CONVERSA MAIS RECENTE ===
const chatIdMaisRecente = conversasRecentes.length > 0 ? conversasRecentes[0].chat_id : null;
// Se não veio chat_id na entrada, usar o da conversa mais recente
const chatIdFinal = entrada.chat_id || chatIdMaisRecente;

// === ESTRUTURA DE SABOTADORES (v1.3.18) ===
const sabotadoresData = sabotadoresRaw.sabotadores?.sabotadores || sabotadoresRaw.sabotadores || {};
const top3Historicos = Array.isArray(sabotadoresData.top3_historicos) ? sabotadoresData.top3_historicos : [];
const sabotadorAtual = sabotadoresData.sabotador_atual || null;

// === LÓGICA SIMPLIFICADA (v1.3.18) ===
// REGRA: SEMPRE prioriza as informações do sabotador ATUAL
// 1. Se atual está no top 3 → usa o ATUAL (não o top 1)
// 2. Se atual NÃO está no top 3 E intensidade >= 60 → cria quest adicional para o top 1

const top3Ids = top3Historicos.map(s => s.sabotador_id);
const sabotadorAtualId = sabotadorAtual?.sabotador_id || null;
const isAtualNoTop3 = sabotadorAtualId && top3Ids.includes(sabotadorAtualId);
const intensidadeAtual = sabotadorAtual?.intensidade_media || 0;

// === SABOTADOR PRINCIPAL PARA QUEST ===
// SEMPRE usa o sabotador ATUAL se existir (prioridade absoluta)
let sabotadorPrincipal = null;
if (sabotadorAtual && sabotadorAtualId) {
  // Sabotador atual existe → usar ele (independente de estar no top 3)
  sabotadorPrincipal = sabotadorAtual;
} else if (top3Historicos.length > 0) {
  // Sem sabotador atual → usar o top 1 histórico
  sabotadorPrincipal = top3Historicos[0];
}

// === QUEST ADICIONAL (v1.3.18) ===
// Se atual NÃO está no top 3 E intensidade >= 60 → criar quest adicional para o top 1 histórico
const criarQuestAdicional = !isAtualNoTop3 && sabotadorAtual && sabotadorAtualId && intensidadeAtual >= 60 && top3Historicos.length > 0;

// Se criar quest adicional, o sabotador secundário é o top 1 histórico
const sabotadorSecundario = criarQuestAdicional ? top3Historicos[0] : null;

const temSabotadorParaQuest = sabotadorPrincipal !== null;

// Buscar quests do catálogo por categoria (priorizando novas)
// NOTA: Não incluir quests de sabotador do catálogo (v1.3.18)
const questReflexao = questsCatalogoNovas.find(q => q.codigo === 'reflexao_diaria') 
  || questsCatalogoUsadas.find(q => q.codigo === 'reflexao_diaria') 
  || null;

const questsTCC = [
  ...questsCatalogoNovas.filter(q => q.categoria === 'tcc'),
  ...questsCatalogoUsadas.filter(q => q.categoria === 'tcc')
];

const questsEstoicismo = [
  ...questsCatalogoNovas.filter(q => q.categoria === 'estoicismo'),
  ...questsCatalogoUsadas.filter(q => q.categoria === 'estoicismo')
];

const areasVida = getNested(areasRaw, ['areas', 'areas_vida'], []);

// === OBJETIVOS DO USUÁRIO ===
const objetivosData = objetivosRaw.objetivos || {};
const objetivosAtivos = Array.isArray(objetivosData.objetivos_ativos) ? objetivosData.objetivos_ativos : [];

return [{
  json: {
    usuario_id: entrada.usuario_id,
    chat_id: chatIdFinal,
    estagio_jornada: estagioJornada,
    quests_ativas: questsAtivas.filter(q => q.status === 'pendente' || q.status === 'ativa'),
    quests_ja_criadas: questsJaCriadas,
    conversas_recentes: conversasRecentes,

    // === REPROCESSAMENTO (v1.4.1) ===
    is_reprocessamento: isReprocessamento,
    quests_substituiveis: questsSubstituiveis,
    total_palavras_usuario: totalPalavrasUsuario,
    total_quests_hoje: totalQuestsHoje,
    sabotadores_hoje: sabotadoresHoje,
    personalizadas_hoje: personalizadasHoje,
    catalogo_hoje: catalogoHoje,

    // === ESTRUTURA SABOTADORES (v1.3.18) ===
    top3_historicos: top3Historicos,
    sabotador_atual: sabotadorAtual,
    sabotador_principal: sabotadorPrincipal,
    sabotador_secundario: sabotadorSecundario,
    criar_quest_adicional: criarQuestAdicional,
    tem_sabotador: temSabotadorParaQuest,
    is_atual_no_top3: isAtualNoTop3,

    areas_vida_disponiveis: areasVida,
    objetivos_ativos: objetivosAtivos,
    quests_catalogo: {
      reflexao: questReflexao,
      // REMOVIDO: sabotador (v1.3.18 - quests de sabotador são geradas do zero)
      tcc: questsTCC,
      estoicismo: questsEstoicismo,
      todas: questsCatalogoSemSabotador,
      novas: questsCatalogoNovas,
      usadas: questsCatalogoUsadas
    }
  }
}];

const entrada = $input.item.json || {};

// === REPROCESSAMENTO (v1.4.1) ===
const isReprocessamento = entrada.is_reprocessamento === true;
const questsSubstituiveis = entrada.quests_substituiveis || [];
const totalPalavrasUsuario = entrada.total_palavras_usuario || 0;
const totalQuestsHoje = entrada.total_quests_hoje || 0;
const sabotadoresHoje = entrada.sabotadores_hoje || 0;
const personalizadasHoje = entrada.personalizadas_hoje || 0;
const catalogoHoje = entrada.catalogo_hoje || 0;

// Identificar quests substituíveis por tipo
const questPersonalizadaSubstituivel = questsSubstituiveis.find(q => 
  q.tipo === 'personalizada' || 
  (q.contexto_origem && q.contexto_origem.includes('conversa'))
) || null;

const questSabotadorSubstituivel = questsSubstituiveis.find(q => 
  q.tipo === 'sabotador' || q.sabotador_id
) || null;

// === ESTRUTURA SABOTADORES (v1.3.18) ===
const top3Historicos = entrada.top3_historicos || [];
const sabotadorAtual = entrada.sabotador_atual || null;
const sabotadorPrincipal = entrada.sabotador_principal || null;
const sabotadorSecundario = entrada.sabotador_secundario || null;
const criarQuestAdicional = entrada.criar_quest_adicional || false;

const questsCatalogo = entrada.quests_catalogo || {};
const questsJaCriadas = entrada.quests_ja_criadas || [];

// IDs de catálogo já usados
const catalogoIdsUsados = new Set(
  questsJaCriadas
    .filter(q => q.catalogo_id)
    .map(q => q.catalogo_id)
);

// Função genérica para escolher quest do catálogo
const escolherQuestCatalogo = (lista, priorizarNovas = true) => {
  if (!Array.isArray(lista) || lista.length === 0) return null;

  const novas = lista.filter(q => !catalogoIdsUsados.has(q.id));
  const usadas = lista.filter(q => catalogoIdsUsados.has(q.id));

  const candidatas = priorizarNovas && novas.length > 0 ? novas : (usadas.length > 0 ? usadas : lista);

  return candidatas.length > 0 ? candidatas[0] : null;
};

// === v1.3.18: QUESTS DE SABOTADOR SÃO GERADAS DO ZERO ===
// Não usar catálogo para sabotadores - usar insight_atual e contramedida_ativa
// O agente vai gerar a quest baseada no contexto específico

// Escolher quest TCC, Estoicismo ou outras categorias (SEMPRE necessário)
const todasQuests = questsCatalogo.todas || [];
const questsTCCEstoicismoOutras = todasQuests.filter(q => {
  if (q.sabotador_id) return false; // Excluir quests de sabotador
  if (q.codigo === 'reflexao_diaria') return false;
  return q.categoria && q.categoria !== 'personalizada';
});

const questTCCEstoicismoOutras = escolherQuestCatalogo(questsTCCEstoicismoOutras, true);

// Garantir que conversas_recentes seja array
const conversasRecentes = Array.isArray(entrada.conversas_recentes) 
  ? entrada.conversas_recentes 
  : [];

// === SEPARAR CONVERSA ATUAL DO HISTÓRICO ===
const conversaAtual = conversasRecentes.length > 0 
  ? {
      data: conversasRecentes[0].data_conversa || conversasRecentes[0].data || null,
      resumo: conversasRecentes[0].resumo || conversasRecentes[0].resumo_conversa || ''
    }
  : null;

const conversasHistorico = conversasRecentes
  .slice(1, 3)
  .map(c => ({
    data: c.data_conversa || c.data || null,
    resumo: c.resumo || c.resumo_conversa || ''
  }))
  .filter(c => c.data && c.resumo);

// === QUESTS JÁ CRIADAS (títulos para evitar duplicação) ===
const questsJaCriadasTitulos = questsJaCriadas
  .slice(0, 10)
  .map(q => q.titulo || q.config?.titulo || '')
  .filter(t => t && t.trim() !== '');

// === OBJETIVOS DO USUÁRIO ===
const objetivosAtivos = Array.isArray(entrada.objetivos_ativos) ? entrada.objetivos_ativos : [];
const objetivoPadrao = objetivosAtivos.find(o => o.is_padrao) || null;
const objetivosEspecificos = objetivosAtivos.filter(o => !o.is_padrao);

// === ÁREAS DA VIDA (apenas id e nome) ===
const areasDisponiveis = (Array.isArray(entrada.areas_vida_disponiveis) ? entrada.areas_vida_disponiveis : [])
  .map(a => ({ id: a.id || null, nome: a.nome || '' }))
  .filter(a => a.id && a.nome);

// === QUEST TCC RESUMIDA ===
const questTCCResumida = questTCCEstoicismoOutras ? {
  catalogo_id: questTCCEstoicismoOutras.id,
  titulo: questTCCEstoicismoOutras.titulo,
  descricao: questTCCEstoicismoOutras.descricao,
  categoria: questTCCEstoicismoOutras.categoria,
  xp: questTCCEstoicismoOutras.xp
} : null;

// Preparar contexto limpo para o agente
return [{
  json: {
    usuario_id: entrada.usuario_id,
    chat_id: entrada.chat_id,

    // === REPROCESSAMENTO (v1.4.1) ===
    is_reprocessamento: isReprocessamento,
    total_palavras_usuario: totalPalavrasUsuario,
    total_quests_hoje: totalQuestsHoje,
    sabotadores_hoje: sabotadoresHoje,
    personalizadas_hoje: personalizadasHoje,
    catalogo_hoje: catalogoHoje,
    quests_substituiveis: {
      personalizada: questPersonalizadaSubstituivel,
      sabotador: questSabotadorSubstituivel
    },

    // === CONVERSAS ===
    conversa_atual: conversaAtual,
    conversas_historico: conversasHistorico,

    // === ANTI-DUPLICAÇÃO ===
    quests_ja_criadas_titulos: questsJaCriadasTitulos,

    // === ÁREAS ===
    areas_disponiveis: areasDisponiveis,

    // === ESTRUTURA SABOTADORES (v1.3.18) ===
    // Sabotadores com insight/contramedida para o agente gerar quest do zero
    top3_historicos: top3Historicos,
    sabotador_atual: sabotadorAtual,
    sabotador_principal: sabotadorPrincipal,
    sabotador_secundario: sabotadorSecundario,
    criar_quest_adicional: criarQuestAdicional,

    // === OBJETIVOS ===
    objetivos_usuario: {
      padrao: objetivoPadrao ? {
        id: objetivoPadrao.id,
        titulo: objetivoPadrao.titulo,
        area_vida: objetivoPadrao.area_vida
      } : null,
      especificos: objetivosEspecificos.map(o => ({
        id: o.id,
        titulo: o.titulo,
        area_vida: o.area_vida
      }))
    },

    // === QUESTS DO CATÁLOGO (v1.3.18) ===
    // REMOVIDO: sabotador_principal e sabotador_secundario do catálogo
    // Quests de sabotador são geradas do zero pelo agente
    quests_catalogo_escolhidas: {
      tcc_estoicismo_outras: questTCCResumida
    }
  }
}];

const getJson = (nodeName) => {
  const data = $items(nodeName, 0, 0) || [];
  const item = data[0] || {};
  return item.json || {};
};

// Buscar contexto do nó "Montar Contexto" para ter insights_recentes completo
const contextoMontar = getJson("Montar Contexto");
const contextoPreparar = getJson("Preparar Quest do Catálogo");

const rawOutput = $json.output ?? { quests: [] };
let resultado;
if (typeof rawOutput === 'string') {
  try {
    resultado = JSON.parse(rawOutput);
  } catch (error) {
    resultado = { quests: [] };
  }
} else if (rawOutput && typeof rawOutput === 'object') {
  resultado = rawOutput;
} else {
  resultado = { quests: [] };
}

const novasQuests = Array.isArray(resultado.quests) ? resultado.quests : [];

// === CATALOGO_IDS PRÉ-SELECIONADOS (v1.3.17) ===
const questsEscolhidas = contextoPreparar.quests_catalogo_escolhidas || {};
const catalogoIdSabotadorPrincipal = questsEscolhidas.sabotador_principal?.catalogo_id || null;
const catalogoIdSabotadorSecundario = questsEscolhidas.sabotador_secundario?.catalogo_id || null;
const catalogoIdTCC = questsEscolhidas.tcc_estoicismo_outras?.catalogo_id || null;

// === REPROCESSAMENTO (v1.3.19) ===
const isReprocessamento = contextoPreparar.is_reprocessamento || false;
const questsSubstituiveis = contextoPreparar.quests_substituiveis || {};
const totalPalavrasUsuario = contextoPreparar.total_palavras_usuario || 0;

return [{
  json: {
    usuario_id: contextoPreparar.usuario_id,
    chat_id: contextoPreparar.chat_id,
    novas_quests_sugeridas: novasQuests,
    atualizacoes_sugeridas: [],
    sabotadores_recentes: contextoMontar.sabotadores_recentes || [],
    insights_recentes: contextoMontar.insights_recentes || [],
    areas_disponiveis: contextoPreparar.areas_disponiveis || [],
    objetivos_disponiveis: contextoMontar.objetivos_ativos || [],

    // === REPROCESSAMENTO (v1.3.19) ===
    is_reprocessamento: isReprocessamento,
    quests_substituiveis: questsSubstituiveis,
    total_palavras_usuario: totalPalavrasUsuario,

    // === NOVA ESTRUTURA SABOTADORES (v1.3.14) ===
    top3_historicos: contextoPreparar.top3_historicos || [],
    sabotador_atual: contextoPreparar.sabotador_atual || null,
    sabotador_principal: contextoPreparar.sabotador_principal || null,
    criar_quest_adicional: contextoPreparar.criar_quest_adicional || false,

    // === CATALOGO_IDS VÁLIDOS (v1.3.17) ===
    catalogo_id_sabotador_principal: catalogoIdSabotadorPrincipal,
    catalogo_id_sabotador_secundario: catalogoIdSabotadorSecundario,
    catalogo_id_tcc: catalogoIdTCC
  }
}];

const entrada = ($input.item && $input.item.json) ? $input.item.json : {};
const questsAtivas = Array.isArray(entrada.quests_ativas) ? entrada.quests_ativas : [];
const questsJaCriadas = Array.isArray(entrada.quests_ja_criadas) ? entrada.quests_ja_criadas : [];

// === REPROCESSAMENTO (v1.4.1) ===
const isReprocessamento = entrada.is_reprocessamento === true;
const questsSubstituiveis = entrada.quests_substituiveis || {};
const totalPalavrasUsuario = entrada.total_palavras_usuario || 0;
const totalQuestsHoje = entrada.total_quests_hoje || 0;
const sabotadoresHoje = entrada.sabotadores_hoje || 0;
const personalizadasHoje = entrada.personalizadas_hoje || 0;
const catalogoHoje = entrada.catalogo_hoje || 0;

// Quests a substituir (será preenchido durante processamento)
const questsParaSubstituir = [];

// Sabotador atual para comparação
const sabotadorAtualId = entrada.sabotador_atual?.sabotador_id || null;

// === QUESTS OBRIGATÓRIAS (v1.4.1) ===
// Se criar_quest_adicional = true → até 4 quests (1 personalizada + 2 sabotador + 1 TCC)
// Senão → 3 quests (1 personalizada + 1 sabotador + 1 TCC)
const criarQuestAdicional = entrada.criar_quest_adicional || false;
const MAX_QUESTS_DIA = criarQuestAdicional ? 4 : 3;
const MAX_QUESTS_SABOTADOR = criarQuestAdicional ? 2 : 1;

// === CATALOGO_IDS PRÉ-SELECIONADOS (v1.3.17) ===
const catalogoIdSabotadorPrincipal = entrada.catalogo_id_sabotador_principal || null;
const catalogoIdSabotadorSecundario = entrada.catalogo_id_sabotador_secundario || null;
const catalogoIdTCC = entrada.catalogo_id_tcc || null;

// === LÓGICA DE SUBSTITUIÇÃO (v1.4.1) ===
// Flags para controlar quais tipos de quests criar
let criarPersonalizada = true;
let criarSabotador = true;
let criarCatalogo = true;
let sabotadorMudou = false;

if (isReprocessamento) {
  // Quest personalizada: SEMPRE substituir se disponivel
  if (questsSubstituiveis.personalizada) {
    questsParaSubstituir.push({
      tipo: 'personalizada',
      quest_id: questsSubstituiveis.personalizada.id,
      motivo: 'mais_contexto'
    });
    // Criar nova personalizada (vai substituir a antiga)
    criarPersonalizada = true;
  } else if (personalizadasHoje > 0) {
    // Já tem personalizada hoje mas não é substituível (status=ativa)
    criarPersonalizada = false;
  }

  // Quest sabotador: substituir APENAS se sabotador mudou
  if (questsSubstituiveis.sabotador && sabotadorAtualId) {
    const sabotadorAnterior = questsSubstituiveis.sabotador.sabotador_id;
    if (sabotadorAnterior !== sabotadorAtualId) {
      sabotadorMudou = true;
      questsParaSubstituir.push({
        tipo: 'sabotador',
        quest_id: questsSubstituiveis.sabotador.id,
        sabotador_anterior: sabotadorAnterior,
        sabotador_novo: sabotadorAtualId,
        motivo: 'sabotador_mudou'
      });
      criarSabotador = true;
    } else {
      // Sabotador não mudou, não criar nova
      criarSabotador = false;
    }
  } else if (sabotadoresHoje > 0) {
    // Já tem sabotador hoje mas não é substituível
    criarSabotador = false;
  }

  // Quest catálogo: NUNCA substituir
  if (catalogoHoje > 0) {
    criarCatalogo = false;
  }
}

// === CALCULAR QUESTS A CRIAR (v1.4.1) ===
// REGRA CRÍTICA: Máximo 3 ou 4 quests por dia
let questsAindaPodeCriar = MAX_QUESTS_DIA - totalQuestsHoje + questsParaSubstituir.length;
if (questsAindaPodeCriar < 0) questsAindaPodeCriar = 0;

const dedupe = new Set();
const catalogoIdsUsados = new Set();
const titulosUsados = new Set();
const uuidRegex = /^[0-9a-fA-F]{8}-[0-9a-fA-F]{4}-[0-9a-fA-F]{4}-[0-9a-fA-F]{4}-[0-9a-fA-F]{12}$/;

// === CONSTANTES ===
const QUEST_CUSTOM_CATALOGO_ID = '00000000-0000-0000-0000-000000000001';
const SABOTADORES_VALIDOS = new Set([
  'critico', 'insistente', 'prestativo', 'hiper_realizador', 'vitima',
  'hiper_racional', 'hipervigilante', 'inquieto', 'controlador', 'esquivo'
]);

const insightsValidos = new Set(
  (Array.isArray(entrada.insights_recentes) ? entrada.insights_recentes : [])
    .map((item) => (typeof item?.id === 'string' ? item.id.trim().toLowerCase() : null))
    .filter(Boolean)
);

// === VALIDAÇÃO DE ÁREAS DA VIDA ===
const areasValidas = new Set(
  (Array.isArray(entrada.areas_disponiveis) ? entrada.areas_disponiveis : [])
    .map((item) => (typeof item?.id === 'string' ? item.id.trim().toLowerCase() : null))
    .filter(Boolean)
);

// === VALIDAÇÃO DE OBJETIVOS ===
const objetivosValidos = new Set(
  (Array.isArray(entrada.objetivos_disponiveis) ? entrada.objetivos_disponiveis : [])
    .map((item) => (typeof item?.id === 'string' ? item.id.trim().toLowerCase() : null))
    .filter(Boolean)
);

// Preencher sets de duplicatas com quests já criadas
for (const questJaCriada of questsJaCriadas) {
  const titulo = String(questJaCriada?.titulo || '').toLowerCase().trim();
  const catalogoId = questJaCriada?.catalogo_id;

  if (titulo) {
    titulosUsados.add(titulo);
  }

  if (catalogoId && uuidRegex.test(catalogoId)) {
    catalogoIdsUsados.add(catalogoId);
  }

  const contexto = String(questJaCriada?.contexto_origem || '').toLowerCase().trim();
  if (contexto || titulo) {
    dedupe.add(`${contexto}|${titulo}`);
  }
}

// Verificar duplicatas das quests ativas
for (const ativo of questsAtivas) {
  const contexto = String(ativo?.contexto_origem || '').toLowerCase().trim();
  const titulo = String((ativo?.config?.titulo ?? ativo?.titulo ?? '')).toLowerCase().trim();
  const catalogoId = ativo?.catalogo_id;

  if (titulo) {
    titulosUsados.add(titulo);
  }

  if (catalogoId && uuidRegex.test(catalogoId)) {
    catalogoIdsUsados.add(catalogoId);
  }

  if (contexto || titulo) {
    dedupe.add(`${contexto}|${titulo}`);
  }
}

const atualizacoes = Array.isArray(entrada.atualizacoes_sugeridas) ? entrada.atualizacoes_sugeridas : [];
const candidatas = Array.isArray(entrada.novas_quests_sugeridas) ? entrada.novas_quests_sugeridas : [];

// Função para verificar similaridade de títulos (evitar duplicatas)
const tituloSimilar = (titulo1, titulo2) => {
  const t1 = String(titulo1 || '').toLowerCase().trim();
  const t2 = String(titulo2 || '').toLowerCase().trim();

  if (!t1 || !t2) return false;

  const palavras1 = t1.split(/\s+/);
  const palavras2 = t2.split(/\s+/);
  const palavrasComuns = palavras1.filter(p => palavras2.includes(p));
  const similaridade = palavrasComuns.length / Math.max(palavras1.length, palavras2.length);

  return similaridade >= 0.8 || t1.includes(t2) || t2.includes(t1);
};

// === ESTRUTURA PARA PERMITIR ATÉ 2 SABOTADORES (v1.3.15) ===
const questsPorTipo = { 
  personalizada: null, 
  sabotador: [], // Array para permitir até 2
  tcc_estoicismo: null 
};

let sabotadorQuestCount = 0; // Contador para usar IDs corretos

for (const quest of candidatas) {
  if (!quest || typeof quest !== 'object') continue;

  const contexto = String(quest.contexto_origem || '').toLowerCase().trim();
  const titulo = String(quest.titulo || '').toLowerCase().trim();
  const chave = `${contexto}|${titulo}`;

  if (!contexto && !titulo) continue;
  if (dedupe.has(chave)) continue;

  let tituloMuitoSimilar = false;
  for (const tituloUsado of titulosUsados) {
    if (tituloSimilar(titulo, tituloUsado)) {
      tituloMuitoSimilar = true;
      break;
    }
  }
  if (tituloMuitoSimilar) continue;

  // === VALIDAR sabotador_id PRIMEIRO (v1.3.19) ===
  let sabotadorId = null;
  if (typeof quest.sabotador_id === 'string') {
    const trimmed = quest.sabotador_id.trim().toLowerCase();
    if (trimmed) {
      if (SABOTADORES_VALIDOS.has(trimmed) || uuidRegex.test(quest.sabotador_id.trim())) {
        sabotadorId = quest.sabotador_id.trim();
      }
    }
  }

  // === DETECTAR SE É QUEST DE SABOTADOR (prioridade absoluta) ===
  const tipoQuest = String(quest.tipo || '').toLowerCase();
  const isSabotadorQuest = sabotadorId !== null || 
                          tipoQuest === 'sabotador' ||
                          contexto.includes('sabotador') || 
                          contexto.includes('sabot');

  // === DETERMINAR catalogo_id ===
  let catalogoId = null;

  // Quest de sabotador: catalogo_id = null (v1.3.19)
  if (isSabotadorQuest) {
    catalogoId = null; // Sabotador quests são geradas do zero, sem catálogo
  } else if (tipoQuest === 'personalizada' || !quest.catalogo_id) {
    catalogoId = QUEST_CUSTOM_CATALOGO_ID;
  } else if (typeof quest.catalogo_id === 'string') {
    const trimmed = quest.catalogo_id.trim();

    // Para quest TCC: usar o ID pré-selecionado
    if (catalogoIdTCC && tipoQuest === 'tcc') {
      catalogoId = catalogoIdTCC;
    } else if (trimmed && uuidRegex.test(trimmed)) {
      catalogoId = trimmed;
    }

    // Verificar duplicata de catalogo_id
    if (catalogoId && catalogoIdsUsados.has(catalogoId) && catalogoId !== QUEST_CUSTOM_CATALOGO_ID) {
      continue;
    }
  }

  // === VALIDAR area_vida_id contra lista de IDs válidos ===
  let areaVidaId = null;
  if (typeof quest.area_vida_id === 'string') {
    const trimmed = quest.area_vida_id.trim();
    if (trimmed && uuidRegex.test(trimmed) && areasValidas.has(trimmed.toLowerCase())) {
      areaVidaId = trimmed;
    }
  }

  let insightId = null;
  if (typeof quest.insight_id === 'string') {
    const trimmed = quest.insight_id.trim();
    if (trimmed && uuidRegex.test(trimmed) && insightsValidos.has(trimmed.toLowerCase())) {
      insightId = trimmed;
    }
  }

  // === VALIDAR objetivo_id contra lista de IDs válidos ===
  let objetivoId = null;
  if (typeof quest.objetivo_id === 'string') {
    const trimmed = quest.objetivo_id.trim();
    if (trimmed && uuidRegex.test(trimmed) && objetivosValidos.has(trimmed.toLowerCase())) {
      objetivoId = trimmed;
    }
  }

  // === OBJETIVOS_SECUNDARIOS - validar cada um ===
  let objetivosSecundarios = [];
  if (Array.isArray(quest.objetivos_secundarios)) {
    objetivosSecundarios = quest.objetivos_secundarios
      .filter(os => os && typeof os.objetivo_id === 'string' && uuidRegex.test(os.objetivo_id.trim()) && objetivosValidos.has(os.objetivo_id.trim().toLowerCase()))
      .map(os => ({ objetivo_id: os.objetivo_id.trim() }));
  }

  // === REGRA FORÇADA: Quest do catálogo SEM sabotador = SEM objetivos secundários ===
  if (catalogoId && catalogoId !== QUEST_CUSTOM_CATALOGO_ID && !sabotadorId) {
    objetivosSecundarios = [];
  }

  // === DETERMINAR TIPO/CATEGORIA (v1.3.19 - priorizar sabotador_id) ===
  let tipoCategoria = 'personalizada';

  // 1. PRIMEIRO: verificar se é quest de sabotador (tem sabotador_id ou tipo=sabotador)
  if (isSabotadorQuest) {
    tipoCategoria = 'sabotador';
  // 2. DEPOIS: verificar personalizada
  } else if (catalogoId === QUEST_CUSTOM_CATALOGO_ID || tipoQuest === 'personalizada') {
    tipoCategoria = 'personalizada';
  // 3. Verificar reflexão (ignorar)
  } else if (contexto.includes('reflexao') || contexto.includes('reflex')) {
    continue;
  // 4. Demais (TCC, estoicismo, etc)
  } else if (catalogoId) {
    tipoCategoria = 'tcc_estoicismo';
  }

  // === DETERMINAR tipo FINAL (corrigir se necessário) ===
  let tipoFinal = tipoQuest;
  if (isSabotadorQuest) {
    tipoFinal = 'sabotador'; // Forçar tipo correto
  } else if (!tipoFinal || tipoFinal === 'catalogo') {
    tipoFinal = catalogoId === QUEST_CUSTOM_CATALOGO_ID ? 'personalizada' : 'tcc';
  }

  // === DETERMINAR catalogo_id FINAL ===
  // Quest personalizada sem sabotador usa QUEST_CUSTOM_CATALOGO_ID
  // Quest de sabotador usa null
  let catalogoIdFinal = catalogoId;
  if (tipoCategoria === 'personalizada' && !sabotadorId) {
    catalogoIdFinal = QUEST_CUSTOM_CATALOGO_ID;
  } else if (tipoCategoria === 'sabotador') {
    catalogoIdFinal = null; // Sabotador não usa catálogo
  }

  const questProcessada = {
    tipo: tipoFinal,
    catalogo_id: catalogoIdFinal,
    titulo: quest.titulo,
    descricao: quest.descricao,
    base_cientifica: quest.base_cientifica || null,
    contexto_origem: quest.contexto_origem,
    prioridade: quest.prioridade || 'media',
    recorrencia: quest.recorrencia || 'unica',
    prazo_inicio: quest.prazo_inicio || null,
    prazo_fim: quest.prazo_fim || null,
    progresso_meta: quest.progresso_meta ?? 1,
    status_inicial: quest.status_inicial || 'disponivel',
    area_vida_id: areaVidaId,
    sabotador_id: sabotadorId,
    insight_id: insightId,
    objetivo_id: objetivoId,
    objetivos_secundarios: objetivosSecundarios,
    complexidade: quest.complexidade ?? 0,
    payload_extra: quest.payload_extra || {},
    xp_recompensa: quest.xp_recompensa || 40
  };

  // === RESPEITAR FLAGS DE REPROCESSAMENTO (v1.4.1) ===
  if (tipoCategoria === 'personalizada' && !questsPorTipo.personalizada && criarPersonalizada) {
    questsPorTipo.personalizada = questProcessada;
  } else if (tipoCategoria === 'sabotador' && questsPorTipo.sabotador.length < MAX_QUESTS_SABOTADOR && criarSabotador) {
    // Permitir até MAX_QUESTS_SABOTADOR quests de sabotador
    questsPorTipo.sabotador.push(questProcessada);
    sabotadorQuestCount++;
  } else if (tipoCategoria === 'tcc_estoicismo' && !questsPorTipo.tcc_estoicismo && criarCatalogo) {
    questsPorTipo.tcc_estoicismo = questProcessada;
  }

  dedupe.add(chave);
  if (titulo) titulosUsados.add(titulo);
  if (catalogoIdFinal && catalogoIdFinal !== QUEST_CUSTOM_CATALOGO_ID) catalogoIdsUsados.add(catalogoIdFinal);
}

const questsFinais = [];
if (questsPorTipo.personalizada && criarPersonalizada) questsFinais.push(questsPorTipo.personalizada);
// Adicionar todas as quests de sabotador (até 2) se permitido
if (criarSabotador) {
  for (const questSab of questsPorTipo.sabotador) {
    questsFinais.push(questSab);
  }
}
if (questsPorTipo.tcc_estoicismo && criarCatalogo) questsFinais.push(questsPorTipo.tcc_estoicismo);

// Segunda passada para preencher se faltou algum tipo
for (const quest of candidatas) {
  if (questsFinais.length >= MAX_QUESTS_DIA) break;
  if (!quest || typeof quest !== 'object') continue;

  const contexto = String(quest.contexto_origem || '').toLowerCase().trim();
  const titulo = String(quest.titulo || '').toLowerCase().trim();
  const chave = `${contexto}|${titulo}`;

  if (contexto.includes('reflexao') || contexto.includes('reflex')) {
    continue;
  }

  if (dedupe.has(chave)) continue;

  let tituloMuitoSimilar = false;
  for (const tituloUsado of titulosUsados) {
    if (tituloSimilar(titulo, tituloUsado)) {
      tituloMuitoSimilar = true;
      break;
    }
  }
  if (tituloMuitoSimilar) continue;

  const tipoQuest = String(quest.tipo || '').toLowerCase();

  // Validar sabotador_id
  let sabotadorId = null;
  if (typeof quest.sabotador_id === 'string') {
    const trimmed = quest.sabotador_id.trim().toLowerCase();
    if (trimmed) {
      if (SABOTADORES_VALIDOS.has(trimmed) || uuidRegex.test(quest.sabotador_id.trim())) {
        sabotadorId = quest.sabotador_id.trim();
      }
    }
  }

  const isSabotadorQuest = sabotadorId !== null || tipoQuest === 'sabotador';

  let catalogoId = null;
  if (isSabotadorQuest) {
    catalogoId = null;
  } else if (tipoQuest === 'personalizada' || !quest.catalogo_id) {
    catalogoId = QUEST_CUSTOM_CATALOGO_ID;
  } else if (typeof quest.catalogo_id === 'string') {
    const trimmed = quest.catalogo_id.trim();
    if (trimmed && uuidRegex.test(trimmed)) {
      if (catalogoIdsUsados.has(trimmed) && trimmed !== QUEST_CUSTOM_CATALOGO_ID) {
        continue;
      }
      catalogoId = trimmed;
    }
  }

  let insightId = null;
  if (typeof quest.insight_id === 'string') {
    const trimmed = quest.insight_id.trim();
    if (trimmed && uuidRegex.test(trimmed) && insightsValidos.has(trimmed.toLowerCase())) {
      insightId = trimmed;
    }
  }

  let areaVidaId = null;
  if (typeof quest.area_vida_id === 'string') {
    const trimmed = quest.area_vida_id.trim();
    if (trimmed && uuidRegex.test(trimmed) && areasValidas.has(trimmed.toLowerCase())) {
      areaVidaId = trimmed;
    }
  }

  let objetivoId = null;
  if (typeof quest.objetivo_id === 'string') {
    const trimmed = quest.objetivo_id.trim();
    if (trimmed && uuidRegex.test(trimmed) && objetivosValidos.has(trimmed.toLowerCase())) {
      objetivoId = trimmed;
    }
  }

  let objetivosSecundarios = [];
  if (Array.isArray(quest.objetivos_secundarios)) {
    objetivosSecundarios = quest.objetivos_secundarios
      .filter(os => os && typeof os.objetivo_id === 'string' && uuidRegex.test(os.objetivo_id.trim()) && objetivosValidos.has(os.objetivo_id.trim().toLowerCase()))
      .map(os => ({ objetivo_id: os.objetivo_id.trim() }));
  }

  if (catalogoId && catalogoId !== QUEST_CUSTOM_CATALOGO_ID && !sabotadorId) {
    objetivosSecundarios = [];
  }

  let tipoFinal = tipoQuest;
  if (isSabotadorQuest) {
    tipoFinal = 'sabotador';
  } else if (!tipoFinal || tipoFinal === 'catalogo') {
    tipoFinal = catalogoId === QUEST_CUSTOM_CATALOGO_ID ? 'personalizada' : 'tcc';
  }

  questsFinais.push({
    tipo: tipoFinal,
    catalogo_id: catalogoId,
    titulo: quest.titulo,
    descricao: quest.descricao,
    base_cientifica: quest.base_cientifica || null,
    contexto_origem: quest.contexto_origem,
    prioridade: quest.prioridade || 'media',
    recorrencia: quest.recorrencia || 'unica',
    prazo_inicio: quest.prazo_inicio || null,
    prazo_fim: quest.prazo_fim || null,
    progresso_meta: quest.progresso_meta ?? 1,
    status_inicial: quest.status_inicial || 'disponivel',
    area_vida_id: areaVidaId,
    sabotador_id: sabotadorId,
    insight_id: insightId,
    objetivo_id: objetivoId,
    objetivos_secundarios: objetivosSecundarios,
    complexidade: quest.complexidade ?? 0,
    payload_extra: quest.payload_extra || {},
    xp_recompensa: quest.xp_recompensa || 40
  });

  dedupe.add(chave);
  if (titulo) titulosUsados.add(titulo);
  if (catalogoId && catalogoId !== QUEST_CUSTOM_CATALOGO_ID) catalogoIdsUsados.add(catalogoId);
}

// === LIMITAR PELO MÁXIMO DO DIA (v1.4.1) ===
const questsParaCriar = questsFinais.slice(0, questsAindaPodeCriar);

return [{
  json: {
    usuario_id: entrada.usuario_id || null,
    chat_id: entrada.chat_id || null,
    total_palavras_usuario: totalPalavrasUsuario,
    novas_quests: questsParaCriar,
    quests_para_substituir: questsParaSubstituir,
    atualizacoes,
    estado_inicial: entrada,
    // Debug info (v1.4.1)
    _debug: {
      is_reprocessamento: isReprocessamento,
      criar_quest_adicional: criarQuestAdicional,
      max_quests_dia: MAX_QUESTS_DIA,
      total_quests_hoje: totalQuestsHoje,
      quests_ainda_pode_criar: questsAindaPodeCriar,
      flags: {
        criar_personalizada: criarPersonalizada,
        criar_sabotador: criarSabotador,
        criar_catalogo: criarCatalogo,
        sabotador_mudou: sabotadorMudou
      },
      quests_hoje_por_tipo: {
        personalizadas: personalizadasHoje,
        sabotadores: sabotadoresHoje,
        catalogo: catalogoHoje
      },
      quests_substituiveis: questsSubstituiveis,
      quests_geradas: questsFinais.length,
      quests_enviadas: questsParaCriar.length
    }
  }
}];

