parameters.functionCode:
const s = $items('start')[0]?.json ?? {};
const ctx = $items('Preparar Contexto Memoria')[0]?.json ?? {};
const output = $json.output ?? {};

// Se não houve atualização, retorna null para pular gravação
if (!output.houve_atualizacao) {
  return [{ json: { skip: true, usuario_id: s.usuario_id } }];
}

// Preparar objeto memoria_ia
const memoria = {
  pessoal: output.pessoal || {},
  preferencias: output.preferencias || {},
  fatos_marcantes: (output.fatos_marcantes || []).slice(-10),
  ultima_atualizacao: new Date().toISOString()
};

return [{
  json: {
    skip: false,
    usuario_id: s.usuario_id,
    memoria_ia: memoria
  }
}];
