parameters.functionCode:
const chat = $items('Buscar Chat + Flags')[0]?.json ?? {};
const payload = $json.output ?? {};
const traits = ['openness','conscientiousness','extraversion','agreeableness','neuroticism'];
const scores = traits.map((key) => {
  const data = payload.big_five?.[key] ?? {};
  const score = Number(data.score);
  const confianca = Number(data.confiabilidade);
  return {
    id: key,
    score: Number.isFinite(score) ? score : null,
    confiabilidade: Number.isFinite(confianca) ? confianca : null
  };
});
const sorted = [...scores].sort((a, b) => (b.score ?? 0) - (a.score ?? 0));
const validTraits = new Set(traits);
const originalPrimary = payload.perfil_primario ?? null;
let primary = originalPrimary;
if (!primary || primary === 'misto' || !validTraits.has(primary)) {
  primary = sorted[0]?.id ?? null;
}
let secondary = sorted.find((entry) => entry.id !== primary)?.id ?? null;
if (!secondary && sorted[1]) {
  secondary = sorted[1].id;
}
const sumConfianca = scores.reduce((acc, entry) => acc + (entry.confiabilidade ?? 0), 0);
const confiancaMedia = scores.length ? Math.round(sumConfianca / scores.length) : null;
let dataConversa = null;
if (chat.data_conversa) {
  const parsed = new Date(chat.data_conversa);
  if (!isNaN(parsed)) dataConversa = parsed.toISOString().slice(0, 10);
}
if (!dataConversa) dataConversa = new Date().toISOString().slice(0, 10);
const result = {
  usuario_id: chat.usuario_id ?? null,
  chat_id: chat.chat_id ?? chat.id ?? null,
  data_conversa: dataConversa,
  perfil_original: originalPrimary,
  perfil_primario: primary,
  perfil_secundario: secondary,
  resumo_perfil: payload.resumo_perfil ?? null,
  insuficiente: Boolean(payload.insuficiente),
  confianca_media: confiancaMedia,
  raw_resposta: payload
};
for (const trait of traits) {
  const data = payload.big_five?.[trait] ?? {};
  const score = Number(data.score);
  const confianca = Number(data.confiabilidade);
  result[`score_${trait}`] = Number.isFinite(score) ? score : null;
  result[`confianca_${trait}`] = Number.isFinite(confianca) ? confianca : null;
}
return [{ json: result }];
