const startData = $items('start')[0]?.json ?? {};
const historyItems = $items('Buscar Hist√≥rico BigFive').map((item) => item.json).filter(Boolean);
const formatDate = (value) => {
  if (!value) return null;
  const parsed = new Date(value);
  if (Number.isNaN(parsed.getTime())) return value;
  return parsed.toISOString().slice(0, 10);
};
const historicoOrdenado = historyItems
  .map((entry) => ({
    data: formatDate(entry.data_conversa),
    resumo: entry.resumo_conversa ?? ''
  }))
  .filter((entry) => entry.data || entry.resumo)
  .sort((a, b) => {
    if (!a.data || !b.data) return 0;
    return a.data < b.data ? 1 : -1;
  });
const historicoResumo = historicoOrdenado.length
  ? historicoOrdenado
      .map((entry) => `- ${entry.data ?? 'data indeterminada'}: ${entry.resumo || 'sem resumo dispon√≠vel'}`)
      .join('\n')
  : 'Sem hist√≥rico relevante nos √∫ltimos 5 dias.';
return [
  {
    json: {
      ...startData,
      historico_resumo: historicoResumo,
      historico_itens: historicoOrdenado
    }
  }
];

