parameters.functionCode:
const s = $items('start')[0]?.json ?? {};
const h = $items('Buscar Historico BigFive').map(i => i.json).filter(Boolean);
const fmt = v => { if (!v) return null; const d = new Date(v); return isNaN(d) ? v : d.toISOString().slice(0,10); };
const hist = h.map(e => ({data: fmt(e.data_conversa), resumo: e.resumo_conversa || ''})).filter(e => e.data || e.resumo).sort((a,b) => a.data < b.data ? 1 : -1);
const resumo = hist.length ? hist.slice(0,3).map(e => `- ${e.data}: ${e.resumo}`).join('\n') : 'Sem hist\u00f3rico.';
return [{json: {...s, historico_resumo: resumo}}];

const s = $items('start')[0]?.json ?? {};
const p = $json.output ?? {};
const traits = ['openness','conscientiousness','extraversion','agreeableness','neuroticism'];
const norm = v => { const n = Number(v); return Number.isFinite(n) ? Math.min(100, Math.max(0, Math.round(n >= 0 && n <= 1 ? n * 100 : n))) : null; };
const scores = traits.map(t => ({id: t, score: norm(p.big_five?.[t]?.score), conf: norm(p.big_five?.[t]?.confiabilidade)}));
const sorted = [...scores].sort((a,b) => (b.score||0) - (a.score||0));
let primary = p.perfil_primario;
if (!primary || primary === 'misto' || !traits.includes(primary)) primary = sorted[0]?.id;
const secondary = sorted.find(e => e.id !== primary)?.id || sorted[1]?.id;
const avgConf = scores.length ? Math.round(scores.reduce((a,e) => a + (e.conf||0), 0) / scores.length) : null;
let dt = s.data_conversa ? new Date(s.data_conversa).toISOString().slice(0,10) : new Date().toISOString().slice(0,10);
const r = {usuario_id: s.usuario_id, chat_id: s.chat_id, data_conversa: dt, perfil_original: p.perfil_primario, perfil_primario: primary, perfil_secundario: secondary, resumo_perfil: p.resumo_perfil, insuficiente: Boolean(p.insuficiente), confianca_media: avgConf, raw_resposta: p};
traits.forEach(t => { r[`score_${t}`] = norm(p.big_five?.[t]?.score); r[`confianca_${t}`] = norm(p.big_five?.[t]?.confiabilidade); });
return [{json: r}];

