parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "titulo": {
      "type": "string",
      "description": "Título curto para push (max 50 chars)"
    },
    "corpo_push": {
      "type": "string",
      "description": "Versão curta para push (max 100 chars)"
    },
    "corpo_whatsapp": {
      "type": "string",
      "description": "Mensagem completa para WhatsApp"
    },
    "contexto_mentor": {
      "type": "string",
      "description": "Contexto para continuidade se usuário responder"
    },
    "subtipo": {
      "type": "string",
      "description": "Subtipo específico (ex: conversa ou primeira_conversa)"
    }
  },
  "required": [
    "titulo",
    "corpo_push",
    "corpo_whatsapp",
    "contexto_mentor",
    "subtipo"
  ]
}

