// Verificações Sem Agente v1.0
// R1: Onboarding (<=3 conversas)
// R7: Limite diário (2/dia)
// R7b: Limite total (15 ativas/disponíveis)

const ctx = $('Buscar Contexto').first().json.contexto || {};

const conversasFinalizadas = ctx.conversas_finalizadas || 0;
const questsHoje = ctx.quests_hoje || 0;
const questsAtivasDisponiveis = ctx.quests_ativas_disponiveis || 0;
const humor = ctx.humor || 6;
const energia = ctx.energia || 6;

// R1: Onboarding
if (conversasFinalizadas <= 3) {
  return [{
    json: {
      pode_processar: false,
      motivo: 'onboarding',
      mensagem: `Usuário em onboarding (${conversasFinalizadas}/3 conversas)`,
      contexto: ctx
    }
  }];
}

// R7b: Limite total (15)
if (questsAtivasDisponiveis >= 15) {
  return [{
    json: {
      pode_processar: false,
      motivo: 'limite_total',
      mensagem: `Limite de 15 quests atingido (${questsAtivasDisponiveis})`,
      contexto: ctx
    }
  }];
}

// R7: Limite diário (2)
const limiteDiarioAtingido = questsHoje >= 2;
const podecriarHoje = Math.max(0, 2 - questsHoje);

return [{
  json: {
    pode_processar: true,
    limite_diario_atingido: limiteDiarioAtingido,
    pode_criar_hoje: podecriarHoje,
    quests_hoje: questsHoje,
    quests_ativas_disponiveis: questsAtivasDisponiveis,
    conversas_finalizadas: conversasFinalizadas,
    humor: humor,
    energia: energia,
    contexto: ctx
  }
}];

// Saída quando não pode processar
const input = $input.item.json || {};

return [{
  json: {
    sucesso: false,
    motivo: input.motivo || 'bloqueado',
    mensagem: input.mensagem || 'Não foi possível processar',
    quests_criadas: []
  }
}];

