// Processar resultado do agente roteador
const verificacao = $('Verificar Limites').first().json || {};
const roteadorOutput = $json.output || {};

let resultado;
if (typeof roteadorOutput === 'string') {
  try {
    resultado = JSON.parse(roteadorOutput);
  } catch (e) {
    resultado = { pode_criar: false, destinos: [], justificativa: 'Erro ao parsear resposta do agente' };
  }
} else {
  resultado = roteadorOutput;
}

const destinos = Array.isArray(resultado.destinos) ? resultado.destinos : [];
const pedidoExplicito = resultado.pedido_explicito === true;

// Aplicar limite diário (exceto se pedido explícito)
let destinosFinais = destinos;
if (verificacao.limite_diario_atingido && !pedidoExplicito) {
  destinosFinais = [];
}

// Ordenar por prioridade
destinosFinais.sort((a, b) => (a.prioridade || 99) - (b.prioridade || 99));

// Limitar a 2 por dia (exceto pedido explícito)
if (!pedidoExplicito) {
  const podecriar = verificacao.pode_criar_hoje || 0;
  destinosFinais = destinosFinais.slice(0, podecriar);
}

return [{
  json: {
    usuario_id: verificacao.contexto?.usuario_id,
    chat_id: verificacao.contexto?.chat_id,
    pode_criar: destinosFinais.length > 0,
    pedido_explicito: pedidoExplicito,
    destinos: destinosFinais,
    excluidos: resultado.excluidos || [],
    justificativa: resultado.justificativa || '',
    contexto_completo: verificacao.contexto
  }
}];

// Preparar itens para loop de destinos
const input = $input.item.json || {};
const destinos = input.destinos || [];

return destinos.map((destino, index) => ({
  json: {
    usuario_id: input.usuario_id,
    chat_id: input.chat_id,
    destino: destino,
    index: index,
    total: destinos.length,
    contexto_completo: input.contexto_completo
  }
}));

// Saída quando não há destinos
const input = $input.item.json || {};

return [{
  json: {
    sucesso: true,
    motivo: 'sem_destinos',
    mensagem: input.justificativa || 'Nenhuma quest a criar',
    excluidos: input.excluidos || [],
    quests_criadas: []
  }
}];
