// Processar resultado do agente roteador
const verificacao = $('Verificar Limites').first().json || {};
const roteadorOutput = $json.output || {};

let resultado;
if (typeof roteadorOutput === 'string') {
  try {
    resultado = JSON.parse(roteadorOutput);
  } catch (e) {
    resultado = { pode_criar: false, destinos: [], justificativa: 'Erro ao parsear resposta do agente' };
  }
} else {
  resultado = roteadorOutput;
}

const destinos = Array.isArray(resultado.destinos) ? resultado.destinos : [];
const pedidoExplicito = resultado.pedido_explicito === true;

// Aplicar limite diário (exceto se pedido explícito)
let destinosFinais = destinos;
if (verificacao.limite_diario_atingido && !pedidoExplicito) {
  destinosFinais = [];
}

// Ordenar por prioridade
destinosFinais.sort((a, b) => (a.prioridade || 99) - (b.prioridade || 99));

// Limitar a 2 por dia (exceto pedido explícito)
if (!pedidoExplicito) {
  const podecriar = verificacao.pode_criar_hoje || 0;
  destinosFinais = destinosFinais.slice(0, podecriar);
}

return [{
  json: {
    usuario_id: verificacao.contexto?.usuario_id,
    chat_id: verificacao.contexto?.chat_id,
    pode_criar: destinosFinais.length > 0,
    pedido_explicito: pedidoExplicito,
    destinos: destinosFinais,
    excluidos: resultado.excluidos || [],
    justificativa: resultado.justificativa || '',
    contexto_completo: verificacao.contexto
  }
}];

// Preparar itens para loop de destinos v3.0
// Usa dados estruturados (resumo + emoções + sabotador)

const input = $input.item.json || {};
const destinos = input.destinos || [];
const ctx = input.contexto_completo || {};

// Mapear nomes amigáveis dos sabotadores
const SABOTADOR_NOMES = {
  'critico': 'Crítico Interior',
  'controlador': 'Controlador',
  'esquivo': 'Esquivo',
  'hiper_realizador': 'Hiper-Realizador',
  'inquieto': 'Inquieto',
  'vitima': 'Vítima',
  'hiper_racional': 'Hiper-Racional',
  'prestativo': 'Prestativo',
  'hiper_vigilante': 'Hiper-Vigilante',
  'hipervigilante': 'Hiper-Vigilante',
  'insatisfeito': 'Insatisfeito'
};

// Sabotador (prioriza conversa atual > histórico)
const sabotadorConversa = ctx.sabotador_conversa || {};
const sabotadorHistorico = ctx.sabotador_historico || {};
const sabotadorPrincipal = sabotadorConversa?.sabotador_id 
  ? sabotadorConversa 
  : sabotadorHistorico;

const sabotadorId = sabotadorPrincipal?.sabotador_id || null;
const sabotadorNome = sabotadorId ? (SABOTADOR_NOMES[sabotadorId] || sabotadorId) : null;

// Emoções formatadas
const emocoes = ctx.emocoes || [];
const emocoesTexto = emocoes.map(e => `${e.emocao} (${e.intensidade}%): ${e.contexto || ''}`).join('; ');

// Montar contexto enriquecido (dados estruturados)
const contextoEnriquecido = {
  // Resumo processado (economiza tokens!)
  resumo_conversa: ctx.resumo_conversa || null,
  pedido_quest: ctx.pedido_quest || null,

  // Estado emocional
  humor: ctx.humor || 6,
  energia: ctx.energia || 6,
  justificativa_humor: ctx.justificativa_humor || null,
  justificativa_energia: ctx.justificativa_energia || null,

  // Emoções detectadas
  emocoes: emocoes,
  emocoes_texto: emocoesTexto || null,

  // Sabotador
  sabotador_id: sabotadorId,
  sabotador_nome: sabotadorNome,
  intensidade: sabotadorPrincipal?.intensidade || sabotadorPrincipal?.intensidade_media || 0,
  insight_atual: sabotadorPrincipal?.insight_atual || null,
  contramedida_ativa: sabotadorPrincipal?.contramedida_ativa || null,

  // Objetivos
  objetivos_especificos: ctx.objetivos_especificos || [],

  // Anti-duplicação
  quests_existentes: ctx.quests_existentes || [],

  // Metadados
  usuario_id: ctx.usuario_id,
  chat_id: ctx.chat_id
};

return destinos.map((destino, index) => ({
  json: {
    usuario_id: input.usuario_id,
    chat_id: input.chat_id,
    destino: destino,
    index: index,
    total: destinos.length,
    contexto: contextoEnriquecido
  }
}));

// Saída quando não há destinos
const input = $input.item.json || {};

return [{
  json: {
    sucesso: true,
    motivo: 'sem_destinos',
    mensagem: input.justificativa || 'Nenhuma quest a criar',
    excluidos: input.excluidos || [],
    quests_criadas: []
  }
}];
