// Anti-Duplicação: Keywords + Cooldown
// Jaccard similarity para títulos

function extrairKeywords(texto) {
  if (!texto) return new Set();
  return new Set(
    texto.toLowerCase()
      .normalize('NFD').replace(/[\u0300-\u036f]/g, '') // remove acentos
      .split(/\s+/)
      .filter(w => w.length > 3)
      .filter(w => !['para', 'com', 'uma', 'seu', 'sua', 'que', 'sobre'].includes(w))
  );
}

function calcularSimilaridade(titulo1, titulo2) {
  const k1 = extrairKeywords(titulo1);
  const k2 = extrairKeywords(titulo2);

  if (k1.size === 0 || k2.size === 0) return 0;

  const intersecao = [...k1].filter(w => k2.has(w)).length;
  const uniao = new Set([...k1, ...k2]).size;

  return intersecao / uniao; // Jaccard
}

const entrada = $('start').first().json;
const catalogo = $input.all().map(i => i.json);
const existentes = $items('Buscar Existentes', 0, 0).map(i => i.json);

const contexto = entrada.contexto || {};
const humor = contexto.humor || 6;
const energia = contexto.energia || 6;

// Temas baseados no contexto
const temas = [];
if (humor < 4 || energia < 4) temas.push('regulacao_emocional');
if (humor < 5 || energia < 5) temas.push('bem_estar');

// Filtrar catálogo por temas EXCLUINDO já usados
const catalogoIdsUsados = new Set(existentes.map(e => e.catalogo_id).filter(Boolean));
const catalogoDisponivel = catalogo.filter(q => !catalogoIdsUsados.has(q.id));

// Verificar duplicação por keywords nos últimos 14 dias
const agora = new Date();
const existentesRecentes = existentes.filter(e => {
  const diff = (agora - new Date(e.ativado_em)) / (1000 * 60 * 60 * 24);
  return diff <= 14;
});

const bloqueios = [];
for (const quest of catalogoDisponivel) {
  for (const existente of existentesRecentes) {
    const sim = calcularSimilaridade(quest.titulo, existente.titulo);
    if (sim > 0.5) {
      bloqueios.push({
        catalogo_id: quest.id,
        motivo: 'similaridade_alta',
        similaridade: sim.toFixed(2),
        quest_existente: existente.titulo
      });
    }
  }
}

// Filtrar catálogo final (sem bloqueados)
const catalogoIdsBloqueados = new Set(bloqueios.map(b => b.catalogo_id));
const catalogoFinal = catalogoDisponivel.filter(q => !catalogoIdsBloqueados.has(q.id));

const podeCriar = catalogoFinal.length > 0;

return [{
  json: {
    usuario_id: entrada.usuario_id,
    chat_id: entrada.chat_id,
    pode_criar: podeCriar,
    motivo: podeCriar ? 'ok' : 'sem_opcoes_disponiveis',
    catalogo_disponivel: catalogoFinal,
    bloqueios: bloqueios,
    contexto: contexto
  }
}];

// Saída sem criar
const input = $input.item.json || {};

return [{
  json: {
    sucesso: false,
    tipo: 'mentalidade',
    motivo: input.motivo || 'bloqueado',
    bloqueios: input.bloqueios || [],
    quest_criada: null
  }
}];

// Selecionar quest do catálogo (prioridade humor/energia)
const input = $input.item.json || {};
const catalogo = input.catalogo_disponivel || [];
const contexto = input.contexto || {};

if (catalogo.length === 0) {
  throw new Error('Nenhuma quest disponível no catálogo');
}

// Priorizar por humor/energia
let questSelecionada;
if (contexto.humor < 4 || contexto.energia < 4) {
  // Priorizar TCC
  questSelecionada = catalogo.find(q => q.categoria === 'tcc') || catalogo[0];
} else if (contexto.humor < 5 || contexto.energia < 5) {
  // Priorizar Estoicismo ou Boas Práticas
  questSelecionada = catalogo.find(q => q.categoria === 'estoicismo') 
    || catalogo.find(q => q.categoria === 'boa_pratica_geral')
    || catalogo[0];
} else {
  // Qualquer uma
  questSelecionada = catalogo[0];
}

return [{
  json: {
    usuario_id: input.usuario_id,
    chat_id: input.chat_id,
    quest_catalogo: questSelecionada,
    contexto: contexto
  }
}];

// Combinar quest adaptada com dados originais
const entrada = $items('Selecionar Quest', 0, 0)[0].json;
const adaptado = $input.item.json;

return [{
  json: {
    usuario_id: entrada.usuario_id,
    catalogo_id: entrada.quest_catalogo.id,
    titulo: adaptado.titulo,
    descricao: adaptado.descricao,
    contexto: entrada.contexto,
    base_cientifica: entrada.quest_catalogo.base_cientifica
  }
}];

