return [{
  json: {
    sucesso: true,
    tipo: 'mentalidade',
    quest_criada: {
      id: quest.id,
      catalogo_id: quest.catalogo_id,
      titulo: quest.config?.titulo || quest.catalogo_titulo,
      categoria: quest.categoria,
      status: quest.status
    }
  }
}];

