const entrada = $input.item.json || {};
const contexto = entrada.contexto || {};

const resumo = contexto.resumo_conversa || '';
const objetivos = contexto.objetivos_especificos || [];

if (objetivos.length === 0) {
  return [{
    json: {
      pode_criar: false,
      motivo: 'sem_objetivos',
      usuario_id: entrada.usuario_id
    }
  }];
}

// Usa resumo processado (sem validar tamanho - expert já validou)
return [{
  json: {
    pode_criar: true,
    usuario_id: entrada.usuario_id,
    chat_id: entrada.chat_id,
    objetivo_selecionado: objetivos[0],
    resumo_conversa: resumo || 'conversa de reflexão',
    contexto: contexto
  }
}];

const input = $input.item.json || {};
return [{
  json: {
    sucesso: false,
    tipo: 'objetivos',
    motivo: input.motivo || 'bloqueado',
    quest_criada: null
  }
}];

