const entrada = $items('Pode Criar?', 0, 0)[0].json;
const criado = $input.item.json;

return [{
  json: {
    usuario_id: entrada.usuario_id,
    objetivo_id: entrada.objetivo_selecionado.id,
    area_vida_id: entrada.objetivo_selecionado.area_vida_id,
    titulo: criado.titulo,
    descricao: criado.descricao
  }
}];

const quest = $input.item.json || {};
return [{
  json: {
    sucesso: true,
    tipo: 'objetivos',
    quest_criada: {
      id: quest.id,
      objetivo_id: quest.objetivo_id,
      titulo: quest.config?.titulo,
      status: quest.status
    }
  }
}];
