const entrada = $input.item.json || {};
const contexto = entrada.contexto || {};

// Usa pedido_quest do expert de resumo
const pedidoQuest = contexto.pedido_quest || '';

if (!pedidoQuest || pedidoQuest.length < 5) {
  return [{
    json: {
      pode_criar: false,
      motivo: 'sem_pedido_quest',
      usuario_id: entrada.usuario_id
    }
  }];
}

return [{
  json: {
    pode_criar: true,
    usuario_id: entrada.usuario_id,
    chat_id: entrada.chat_id,
    pedido: pedidoQuest,
    resumo_conversa: contexto.resumo_conversa || '',
    contexto: contexto
  }
}];

const input = $input.item.json || {};
return [{
  json: {
    sucesso: false,
    tipo: 'personalizada',
    motivo: input.motivo || 'bloqueado',
    quest_criada: null
  }
}];

const quest = $input.item.json || {};
return [{
  json: {
    sucesso: true,
    tipo: 'personalizada',
    quest_criada: {
      id: quest.id,
      titulo: quest.config?.titulo,
      status: quest.status
    }
  }
}];
