// Verificar Duplicação v3 - Abordagem Híbrida
// REGRA: Pode criar se tem dados personalizados OU catálogo disponível

const entrada = $('start').first().json;
const catalogo = $items('Buscar Contramedidas', 0, 0).map(i => i.json);
const existentes = $input.all().map(i => i.json);

const contexto = entrada.contexto || {};
const sabotadorId = contexto.sabotador_id;

if (!sabotadorId) {
  return [{
    json: {
      usuario_id: entrada.usuario_id,
      pode_criar: false,
      motivo: 'sem_sabotador_detectado'
    }
  }];
}

// Verificar cooldown: mesmo sabotador em 7 dias
const temQuestRecente = existentes.some(e => e.sabotador_id === sabotadorId);

if (temQuestRecente) {
  return [{
    json: {
      usuario_id: entrada.usuario_id,
      pode_criar: false,
      motivo: 'cooldown_7_dias',
      sabotador_id: sabotadorId
    }
  }];
}

// Verificar se tem dados personalizados (insight + contramedida do expert)
const temInsight = contexto.insight_atual && contexto.insight_atual.length > 10;
const temContramedida = contexto.contramedida_ativa && contexto.contramedida_ativa.length > 10;
const temDadosPersonalizados = temInsight && temContramedida;

// Excluir catálogos já usados
const catalogoIdsUsados = new Set(existentes.map(e => e.catalogo_id).filter(Boolean));
const catalogoDisponivel = catalogo.filter(q => !catalogoIdsUsados.has(q.id));

// NOVA REGRA: pode criar se tem dados personalizados OU catálogo
const podeCriar = temDadosPersonalizados || catalogoDisponivel.length > 0;

let motivo = 'ok';
if (!podeCriar) {
  motivo = 'sem_dados_personalizados_e_sem_catalogo';
}

return [{
  json: {
    usuario_id: entrada.usuario_id,
    chat_id: entrada.chat_id,
    pode_criar: podeCriar,
    motivo: motivo,
    catalogo_disponivel: catalogoDisponivel,
    tem_dados_personalizados: temDadosPersonalizados,
    contexto: contexto
  }
}];

const input = $input.item.json || {};
return [{
  json: {
    sucesso: false,
    tipo: 'sabotador',
    motivo: input.motivo || 'bloqueado',
    quest_criada: null
  }
}];

// Selecionar Contramedida v3 - Abordagem Híbrida
// FONTE 1: usuarios_sabotadores → insight + contramedida personalizada
// FONTE 2: quests_catalogo → base_cientifica (fundamentação)

const input = $input.item.json || {};
const catalogo = input.catalogo_disponivel || [];
const contexto = input.contexto || {};

// Dados personalizados do expert sabotador
const insightPersonalizado = contexto.insight_atual || null;
const contramedidaPersonalizada = contexto.contramedida_ativa || null;

// Se não tem dados personalizados, precisa de catálogo
const temDadosPersonalizados = insightPersonalizado && contramedidaPersonalizada;

if (!temDadosPersonalizados && catalogo.length === 0) {
  throw new Error('Sem dados personalizados e sem contramedidas no catálogo');
}

// Selecionar quest do catálogo (para base_cientifica)
let questCatalogo = null;
if (catalogo.length > 0) {
  // Tentar encontrar contramedida relacionada à contramedida personalizada
  if (contramedidaPersonalizada) {
    const keywords = contramedidaPersonalizada.toLowerCase().split(/\s+/).filter(w => w.length > 4);
    questCatalogo = catalogo.find(q => {
      const desc = (q.descricao || '').toLowerCase();
      return keywords.some(k => desc.includes(k));
    });
  }
  // Fallback: primeira disponível
  if (!questCatalogo) {
    questCatalogo = catalogo[0];
  }
}

// Montar objeto híbrido
return [{
  json: {
    usuario_id: input.usuario_id,
    chat_id: input.chat_id,

    // PERSONALIZAÇÃO (usuarios_sabotadores)
    insight_personalizado: insightPersonalizado,
    contramedida_personalizada: contramedidaPersonalizada,
    tem_dados_personalizados: temDadosPersonalizados,

    // FUNDAMENTAÇÃO (quests_catalogo)
    quest_catalogo: questCatalogo,
    base_cientifica: questCatalogo?.base_cientifica || null,

    // Contexto completo
    contexto: contexto
  }
}];

// Preparar Insert - Quest Sabotador v3 (Híbrido)
// Combina: personalização do expert + base científica do catálogo

const entrada = $items('Selecionar Contramedida', 0, 0)[0].json;
const adaptado = $input.item.json;

// Catálogo pode ser null se não houver disponível
const catalogoId = entrada.quest_catalogo?.id || null;
const baseCientifica = entrada.base_cientifica || null;

return [{
  json: {
    usuario_id: entrada.usuario_id,
    catalogo_id: catalogoId,
    sabotador_id: entrada.contexto.sabotador_id,
    titulo: adaptado.output.titulo,
    descricao: adaptado.output.descricao,
    base_cientifica: baseCientifica
  }
}];

const quest = $input.item.json || {};
return [{
  json: {
    sucesso: true,
    tipo: 'sabotador',
    quest_criada: {
      id: quest.id,
      catalogo_id: quest.catalogo_id,
      sabotador_id: quest.sabotador_id,
      titulo: quest.config?.titulo || quest.catalogo_titulo,
      categoria: quest.categoria,
      status: quest.status
    }
  }
}];
