const entrada = $input.item.json || {};
const usuarioId = entrada.usuario_id || entrada.id || $json.usuario_id;
if (!usuarioId || typeof usuarioId !== 'string' || !usuarioId.trim()) {
  throw new Error('usuario_id obrigatório');
}
const rawStringFlag = entrada.recalcular_completo ?? $json.recalcular_completo ?? entrada.force_recalcular ?? entrada.force ?? null;
const rawBooleanFlag = entrada.calcular_apenas_novos ?? $json.calcular_apenas_novos;
const recalcularCompleto = rawStringFlag === null
  ? (rawBooleanFlag === false)
  : (typeof rawStringFlag === 'string'
      ? ['1','true','yes','y','sim'].includes(rawStringFlag.trim().toLowerCase())
      : Boolean(rawStringFlag));
return [{
  json: {
    usuario_id: usuarioId.trim(),
    recalcular_completo: recalcularCompleto,
  },
}];

const getItem = (nodeName) => {
  const list = $items(nodeName, 0, 0) || [];
  return list[0]?.json || null;
};
const getItems = (nodeName) => {
  const list = $items(nodeName) || [];
  return list.map((entry) => entry?.json || {});
};
const estado = getItem('Buscar Estado');
if (!estado) {
  throw new Error('Estado do usuário não encontrado');
}
const conversas = getItems('Buscar Conversas');
const metas = [
  { codigo: 'streak_003', dias: 3, bonus: 40, titulo: 'Sequência de 3 conversas' },
  { codigo: 'streak_005', dias: 5, bonus: 60, titulo: 'Sequência de 5 conversas' },
  { codigo: 'streak_007', dias: 7, bonus: 90, titulo: 'Sequência de 7 conversas' },
  { codigo: 'streak_010', dias: 10, bonus: 130, titulo: 'Sequência de 10 conversas' },
  { codigo: 'streak_015', dias: 15, bonus: 180, titulo: 'Sequência de 15 conversas' },
  { codigo: 'streak_020', dias: 20, bonus: 250, titulo: 'Sequência de 20 conversas' },
  { codigo: 'streak_025', dias: 25, bonus: 340, titulo: 'Sequência de 25 conversas' },
  { codigo: 'streak_030', dias: 30, bonus: 450, titulo: 'Sequência de 30 conversas' },
];
const metaTargets = metas.map((meta) => ({ codigo: meta.codigo, dias: meta.dias }));
const XP_POR_DIA = 75;
const BONUS_PRIMEIRA_CONVERSA = 75;
const toTimestamp = (value) => {
  if (!value && value !== 0) return null;
  const date = new Date(value);
  if (Number.isNaN(date.getTime())) return null;
  return date.getTime();
};
const toDay = (value) => {
  if (!value && value !== 0) return null;
  const date = new Date(value);
  if (Number.isNaN(date.getTime())) return null;
  return new Date(Date.UTC(date.getUTCFullYear(), date.getUTCMonth(), date.getUTCDate()));
};
const parseStatus = () => {
  const raw = estado.sequencia_status;
  if (!raw) return { estado: 'ativa', reinicios: 0, alvo_conversas: metaTargets[0].dias };
  if (typeof raw === 'object') {
    return { estado: 'ativa', alvo_conversas: metaTargets[0].dias, ...raw };
  }
  try {
    return { estado: 'ativa', alvo_conversas: metaTargets[0].dias, ...(JSON.parse(raw) || {}) };
  } catch (error) {
    return { estado: 'ativa', reinicios: 0, alvo_conversas: metaTargets[0].dias };
  }
};
const determineNextMeta = (streak) => {
  for (const meta of metaTargets) {
    if (streak < meta.dias) {
      return meta;
    }
  }
  return metaTargets[metaTargets.length - 1];
};
const status = parseStatus();
const msDia = 24 * 60 * 60 * 1000;
const conversasOrdenadas = conversas
  .map((entrada) => {
    const timestamp = toTimestamp(entrada.atualizado_em || entrada.data_conversa || entrada.dia);
    const diaISO = toDay(entrada.atualizado_em || entrada.data_conversa || entrada.dia);
    const diaISOTexto = diaISO ? diaISO.toISOString() : null;
    return { ...entrada, timestamp, diaISO, diaISOTexto };
  })
  .filter((entrada) => entrada.diaISO && entrada.diaISOTexto && Number.isFinite(entrada.timestamp))
  .sort((a, b) => (a.timestamp || 0) - (b.timestamp || 0));
const diasUnicosMap = new Map();
for (const conversa of conversasOrdenadas) {
  const chave = conversa.diaISOTexto;
  if (!chave) continue;
  if (!diasUnicosMap.has(chave) || (conversa.timestamp || 0) < (diasUnicosMap.get(chave).timestamp || 0)) {
    diasUnicosMap.set(chave, conversa);
  }
}
const diasUnicos = Array.from(diasUnicosMap.values()).sort((a, b) => (a.timestamp || 0) - (b.timestamp || 0));
const ultimaRegistradaTimestamp = toTimestamp(estado.ultima_conversa_em);
const ultimaDiaRegistrada = toDay(estado.ultima_conversa_em);
let xpBaseIncrement = 0;
let xpBonusIncrement = 0;
let sequenciaAtual = 0;
let sequenciaRecordeCalculado = 0;
let reinicios = Number(status.reinicios) || 0;
let metaAlcancadaIdxAnterior = metas.findIndex((meta) => meta.codigo === estado.meta_sequencia_codigo);
if (metaAlcancadaIdxAnterior < 0) metaAlcancadaIdxAnterior = -1;
let metaAlcancadaIdx = metaAlcancadaIdxAnterior;
let alvoIdx = Math.max(metaAlcancadaIdxAnterior + 1, 0);
const resetMetaIndices = () => {
  metaAlcancadaIdxAnterior = -1;
  metaAlcancadaIdx = -1;
  alvoIdx = 0;
};
const applyReset = () => {
  if (sequenciaAtual > 0) {
    reinicios += 1;
  }
  sequenciaAtual = 0;
  resetMetaIndices();
};
let ultimoDiaSequencia = ultimaDiaRegistrada;
let ultimaTimestampProcessado = ultimaRegistradaTimestamp || null;
let diasNovos = 0;
const diasNovosDetalhes = [];
const streakEvents = [];
for (const diaInfo of diasUnicos) {
  const diaISO = diaInfo.diaISO;
  const timestamp = diaInfo.timestamp;
  const diaEhNovo = !ultimaDiaRegistrada || diaISO.getTime() > ultimaDiaRegistrada.getTime();
  if (diaEhNovo) {
    xpBaseIncrement += XP_POR_DIA;
    diasNovos += 1;
    diasNovosDetalhes.push({
      dia: diaISO.toISOString(),
      conversa_id: (diaInfo && (diaInfo.conversa_id || diaInfo.id)) ? diaInfo.conversa_id || diaInfo.id : null,
      data_conversa: diaInfo?.data_conversa || diaISO.toISOString(),
    });
  }
  if (ultimoDiaSequencia) {
    const diffDias = Math.floor((diaISO.getTime() - ultimoDiaSequencia.getTime()) / msDia);
    if (diffDias > 1) {
      applyReset();
    }
  }
  sequenciaAtual += 1;
  ultimoDiaSequencia = diaISO;
  ultimaTimestampProcessado = timestamp ?? ultimaTimestampProcessado ?? timestamp;
  if (sequenciaAtual > sequenciaRecordeCalculado) {
    sequenciaRecordeCalculado = sequenciaAtual;
  }
  while (alvoIdx < metas.length && sequenciaAtual >= metas[alvoIdx].dias) {
    if (diaEhNovo && alvoIdx > metaAlcancadaIdxAnterior) {
      xpBonusIncrement += metas[alvoIdx].bonus;
      streakEvents.push({
        meta_codigo: metas[alvoIdx].codigo,
        meta_titulo: metas[alvoIdx].titulo,
        xp_bonus: metas[alvoIdx].bonus,
        completado_em: diaISO.toISOString(),
        dias: metas[alvoIdx].dias,
      });
    }
    metaAlcancadaIdx = alvoIdx;
    alvoIdx += 1;
  }
  metaAlcancadaIdxAnterior = metaAlcancadaIdx;
}
const sequenciaRecorde = Math.max(sequenciaRecordeCalculado, Number(estado.sequencia_recorde) || 0);
const metaCodigoAtual = metaAlcancadaIdx >= 0 ? metas[metaAlcancadaIdx].codigo : metas[0].codigo;
const proximaMetaAlvo = determineNextMeta(sequenciaAtual);
const proximaCodigo = proximaMetaAlvo ? proximaMetaAlvo.codigo : null;
const alvoConversas = proximaMetaAlvo ? proximaMetaAlvo.dias : metaTargets[metaTargets.length - 1].dias;
const ultimaConversaISO = ultimoDiaSequencia
  ? ultimoDiaSequencia.toISOString()
  : estado.ultima_conversa_em || null;
const primeiraConversaDesbloqueada = !ultimaDiaRegistrada && diasNovos > 0;
const xpBonusPrimeira = primeiraConversaDesbloqueada ? BONUS_PRIMEIRA_CONVERSA : 0;
const xpBonusTotal = xpBonusIncrement + xpBonusPrimeira;
const xpTotalIncrement = xpBaseIncrement + xpBonusTotal;
return [{
  json: {
    usuario_id: estado.usuario_id,
    xp_ganho: xpTotalIncrement,
    xp_total_increment: xpTotalIncrement,
    xp_base_increment: xpBaseIncrement,
    xp_bonus_increment: xpBonusTotal,
    sequencia_atual: sequenciaAtual,
    sequencia_recorde: sequenciaRecorde,
    meta_sequencia_codigo: metaCodigoAtual,
    proxima_meta_codigo: proximaCodigo,
    ultima_conversa_em: ultimaConversaISO,
    sequencia_status: { ...status, estado: 'ativa', reinicios, alvo_conversas: alvoConversas },
    dias_processados: diasUnicos.length,
    dias_novos: diasNovos,
    dias_novos_detalhes: diasNovosDetalhes,
    primeira_conversa_desbloqueada: primeiraConversaDesbloqueada,
    xp_bonus_primeira: xpBonusPrimeira,
    streaks_conquistas: streakEvents,
  },
}];

const entrada = $items('Validar Entrada', 0, 0) || [];
const payload = entrada[0]?.json || {};
return [{ json: { usuario_id: payload.usuario_id || null } }];

const parseDay = (value) => {
  if (!value) return null;
  const date = new Date(value);
  if (Number.isNaN(date.getTime())) return null;
  return Date.UTC(date.getUTCFullYear(), date.getUTCMonth(), date.getUTCDate());
};

const today = new Date();
const todayUtc = Date.UTC(today.getUTCFullYear(), today.getUTCMonth(), today.getUTCDate());

const normalizeStatus = (raw) => {
  if (raw && typeof raw === 'object') {
    return { ...raw };
  }
  if (typeof raw === 'string') {
    try {
      const parsed = JSON.parse(raw);
      if (parsed && typeof parsed === 'object') {
        return { ...parsed };
      }
    } catch (_err) {
      // ignore
    }
  }
  return {};
};

return $input.all().map(({ json }) => {
  const ultimaConversaoUtc = parseDay(json.ultima_conversa_em);
  const diffDias = ultimaConversaoUtc === null
    ? Number.POSITIVE_INFINITY
    : Math.floor((todayUtc - ultimaConversaoUtc) / 86_400_000);

  if (!Number.isFinite(diffDias) || diffDias > 1) {
    const statusAtual = normalizeStatus(json.sequencia_status);
    const reinicios = Number.isFinite(Number(statusAtual.reinicios))
      ? Number(statusAtual.reinicios) + 1
      : 1;

    json.sequencia_atual = 0;
    json.meta_sequencia_codigo = 'streak_003';
    json.proxima_meta_codigo = 'streak_005';
    json.sequencia_status = {
      estado: 'reiniciada',
      reinicios,
      alvo_conversas: 3,
      completado_em: null,
      ultimo_reset_em: new Date().toISOString(),
    };
  }

  return { json };
});

const estado = $json || {};
const entrada = ($items('Validar Entrada', 0, 0) || [])[0]?.json || {};
const force = Boolean(entrada.recalcular_completo);
const hoje = new Date();
const hojeUtc = Date.UTC(hoje.getUTCFullYear(), hoje.getUTCMonth(), hoje.getUTCDate());
const ultima = estado.ultima_conversa_em ? new Date(estado.ultima_conversa_em) : null;
const ultimaUtc = ultima ? Date.UTC(ultima.getUTCFullYear(), ultima.getUTCMonth(), ultima.getUTCDate()) : null;
const diffDias = ultimaUtc === null ? Number.POSITIVE_INFINITY : Math.max(0, Math.floor((hojeUtc - ultimaUtc) / 86_400_000));
const precisaRecalcular = force || diffDias >= 1;
return [{
  json: {
    ...estado,
    usuario_id: entrada.usuario_id ?? estado.usuario_id ?? null,
    recalcular_completo: force,
    precisa_recalcular: precisaRecalcular,
    dias_desde_ultima_conversa: diffDias,
  },
}];

const entrada = $json || {};
const usuarioId = entrada.usuario_id;
const diasNovos = entrada.dias_novos || 0;
const xpBase = entrada.xp_base_increment || entrada.xp_base_diario || 0;
if (!usuarioId || !diasNovos || !xpBase) {
  return [];
}
const detalhesOrigem = Array.isArray(entrada.dias_novos_detalhes) ? entrada.dias_novos_detalhes : [];
const ocorrencias = detalhesOrigem.map((item) => ({
  dia: item && item.dia ? item.dia : null,
  conversa_id: item && (item.conversa_id || item.id) ? (item.conversa_id || item.id) : null,
  data_conversa: item && item.data_conversa ? item.data_conversa : (item && item.dia ? item.dia : null),
}));
return [{
  json: {
    usuario_id: usuarioId,
    meta_codigo: 'conversa_diaria',
    meta_titulo: 'Conversa diária',
    xp_base_increment: xpBase,
    recorrencia_increment: diasNovos,
    ocorrencias,
  },
}];

const entrada = $json || {};
const usuarioId = entrada.usuario_id;
if (!usuarioId) {
  return [];
}
const conquistas = [];
if (entrada.primeira_conversa_desbloqueada) {
  conquistas.push({
    usuario_id: usuarioId,
    meta_codigo: 'primeira_conversa',
    meta_titulo: 'Primeira conversa',
    xp_base: 0,
    xp_bonus: entrada.xp_bonus_primeira || 75,
    detalhes: {
      desbloqueada_em: entrada.ultima_conversa_em || new Date().toISOString(),
    },
  });
}
const eventos = Array.isArray(entrada.streaks_conquistas) ? entrada.streaks_conquistas : [];
for (const evento of eventos) {
  if (!evento || !evento.meta_codigo) continue;
  conquistas.push({
    usuario_id: usuarioId,
    meta_codigo: evento.meta_codigo,
    meta_titulo: evento.meta_titulo || evento.meta_codigo,
    xp_base: 0,
    xp_bonus: evento.xp_bonus || 0,
    detalhes: {
      completado_em: evento.completado_em || null,
      dias: evento.dias || null,
    },
  });
}
return conquistas.map((item) => ({ json: item }));
