// Retorna resultado do processamento
const verificar = $('Verificar Processamento').item.json;
const xpAtualizado = $('Atualizar XP').item.json;

return [{
  json: {
    usuario_id: verificar.usuario_id,
    chat_id: verificar.chat_id,
    resultado: 'processado',
    xp_ganho: verificar.xp_valor,
    xp_total: xpAtualizado.xp_total,
    data_conversa: verificar.data_conversa
  }
}];

// Retorna resultado quando não processa
const verificar = $('Verificar Processamento').item.json;

return [{
  json: {
    usuario_id: verificar.usuario_id,
    chat_id: verificar.chat_id,
    resultado: verificar.acao,
    xp_ganho: 0,
    xp_total: null,
    data_conversa: verificar.data_conversa
  }
}];
