const getItem = (nodeName) => {
  const list = $items(nodeName, 0, 0) || [];
  return list[0]?.json || null;
};
const getItems = (nodeName) => {
  const list = $items(nodeName) || [];
  return list.map((entry) => entry?.json || {});
};
const estado = getItem('Buscar Estado');
if (!estado) {
  throw new Error('Estado do usuário não encontrado');
}
const conversas = getItems('Buscar Conversas');
const metas = [
  { codigo: 'streak_003', dias: 3, bonus: 40 },
  { codigo: 'streak_005', dias: 5, bonus: 60 },
  { codigo: 'streak_007', dias: 7, bonus: 90 },
  { codigo: 'streak_010', dias: 10, bonus: 130 },
  { codigo: 'streak_015', dias: 15, bonus: 180 },
  { codigo: 'streak_020', dias: 20, bonus: 250 },
  { codigo: 'streak_025', dias: 25, bonus: 340 },
  { codigo: 'streak_030', dias: 30, bonus: 450 },
];
const toDay = (value) => {
  if (!value) return null;
  const date = new Date(value);
  if (Number.isNaN(date.getTime())) return null;
  return new Date(Date.UTC(date.getUTCFullYear(), date.getUTCMonth(), date.getUTCDate()));
};
const parseStatus = () => {
  const raw = estado.sequencia_status;
  if (!raw) return { estado: 'ativa', reinicios: 0, alvo_conversas: metas[0].dias };
  if (typeof raw === 'object') return { estado: 'ativa', alvo_conversas: metas[0].dias, ...raw };
  try {
    return { estado: 'ativa', alvo_conversas: metas[0].dias, ...(JSON.parse(raw) || {}) };
  } catch (error) {
    return { estado: 'ativa', reinicios: 0, alvo_conversas: metas[0].dias };
  }
};
const status = parseStatus();
let xpGanho = 0;
const diasMap = new Map();
const calcXPConversa = (entrada) => {
  const palavras = Number(entrada.total_palavras_usuario) || 0;
  const reflexao = entrada.tem_reflexao === true;
  if (palavras >= 500) return 50;
  if (reflexao) return 35;
  return 20;
};
for (const conversa of conversas) {
  const xp = calcXPConversa(conversa);
  xpGanho += xp;
  const dia = conversa.dia;
  if (!diasMap.has(dia)) {
    diasMap.set(dia, { xp: 0 });
  }
  diasMap.get(dia).xp += xp;
}
const diasOrdenados = Array.from(diasMap.keys()).sort();
let sequenciaAtual = Number(estado.sequencia_atual) || 0;
let sequenciaRecorde = Number(estado.sequencia_recorde) || 0;
let reinicios = Number(status.reinicios) || 0;
let ultimaData = toDay(estado.ultima_conversa_em);
let metaAlcancadaIdx = metas.findIndex((meta) => meta.codigo === estado.meta_sequencia_codigo);
if (metaAlcancadaIdx < 0) metaAlcancadaIdx = -1;
let alvoIdx = Math.max(metaAlcancadaIdx + 1, 0);
const resetMetaProgress = () => {
  metaAlcancadaIdx = -1;
  alvoIdx = 0;
};
const applyReset = () => {
  if (sequenciaAtual > 0) {
    reinicios += 1;
  }
  sequenciaAtual = 0;
  resetMetaProgress();
};
const msDia = 24 * 60 * 60 * 1000;
for (const dia of diasOrdenados) {
  const diaAtual = toDay(dia);
  if (!diaAtual) continue;
  if (ultimaData) {
    const diff = Math.round((diaAtual - ultimaData) / msDia);
    if (diff > 1) {
      applyReset();
    }
    if (diff === 0) {
      ultimaData = diaAtual;
      continue;
    }
  } else {
    resetMetaProgress();
  }
  sequenciaAtual += 1;
  ultimaData = diaAtual;
  if (sequenciaAtual > sequenciaRecorde) {
    sequenciaRecorde = sequenciaAtual;
  }
  while (alvoIdx < metas.length && sequenciaAtual >= metas[alvoIdx].dias) {
    xpGanho += metas[alvoIdx].bonus;
    metaAlcancadaIdx = alvoIdx;
    alvoIdx += 1;
  }
}
const hoje = toDay(new Date());
if (ultimaData && hoje) {
  const diffHoje = Math.round((hoje - ultimaData) / msDia);
  if (diffHoje > 1) {
    applyReset();
  }
}
const metaCodigoAtual = metaAlcancadaIdx >= 0 ? metas[metaAlcancadaIdx].codigo : metas[0].codigo;
const proximaCodigo = alvoIdx < metas.length ? metas[alvoIdx].codigo : null;
const alvoConversas = alvoIdx < metas.length ? metas[alvoIdx].dias : metas[metas.length - 1].dias;
const ultimaConversaISO = ultimaData ? ultimaData.toISOString() : estado.ultima_conversa_em || null;
return [{
  json: {
    usuario_id: estado.usuario_id,
    xp_ganho: xpGanho,
    sequencia_atual: sequenciaAtual,
    sequencia_recorde: sequenciaRecorde,
    meta_sequencia_codigo: metaCodigoAtual,
    proxima_meta_codigo: proximaCodigo,
    ultima_conversa_em: ultimaConversaISO,
    sequencia_status: { ...status, estado: 'ativa', reinicios, alvo_conversas: alvoConversas },
    dias_processados: diasOrdenados.length,
  },
}];

const entrada = $items('Validar Entrada', 0, 0) || [];
const payload = entrada[0]?.json || {};
return [{ json: { usuario_id: payload.usuario_id || null } }];
