const getItem = (nodeName) => {
  const list = $items(nodeName, 0, 0) || [];
  return list[0]?.json || null;
};
const getItems = (nodeName) => {
  const list = $items(nodeName) || [];
  return list.map((entry) => entry?.json || {});
};
const estado = getItem('Buscar Estado');
if (!estado) {
  throw new Error('Estado do usuário não encontrado');
}
const conversas = getItems('Buscar Conversas');
const metas = [
  { codigo: 'streak_003', dias: 3, bonus: 40 },
  { codigo: 'streak_005', dias: 5, bonus: 60 },
  { codigo: 'streak_007', dias: 7, bonus: 90 },
  { codigo: 'streak_010', dias: 10, bonus: 130 },
  { codigo: 'streak_015', dias: 15, bonus: 180 },
  { codigo: 'streak_020', dias: 20, bonus: 250 },
  { codigo: 'streak_025', dias: 25, bonus: 340 },
  { codigo: 'streak_030', dias: 30, bonus: 450 },
];
const metaTargets = metas.map((meta) => ({ codigo: meta.codigo, dias: meta.dias }));
const toTimestamp = (value) => {
  if (!value) return null;
  const date = new Date(value);
  if (Number.isNaN(date.getTime())) return null;
  return date.getTime();
};
const toDay = (value) => {
  if (!value && value !== 0) return null;
  const date = new Date(value);
  if (Number.isNaN(date.getTime())) return null;
  return new Date(Date.UTC(date.getUTCFullYear(), date.getUTCMonth(), date.getUTCDate()));
};
const parseStatus = () => {
  const raw = estado.sequencia_status;
  if (!raw) return { estado: 'ativa', reinicios: 0, alvo_conversas: metaTargets[0].dias };
  if (typeof raw === 'object') {
    return { estado: 'ativa', alvo_conversas: metaTargets[0].dias, ...raw };
  }
  try {
    return { estado: 'ativa', alvo_conversas: metaTargets[0].dias, ...(JSON.parse(raw) || {}) };
  } catch (error) {
    return { estado: 'ativa', reinicios: 0, alvo_conversas: metaTargets[0].dias };
  }
};
const determineNextMeta = (streak) => {
  for (const meta of metaTargets) {
    if (streak < meta.dias) {
      return meta;
    }
  }
  return metaTargets[metaTargets.length - 1];
};
const status = parseStatus();
const msDia = 24 * 60 * 60 * 1000;
const conversasNormalizadas = conversas
  .map((entrada) => {
    const timestamp = toTimestamp(entrada.atualizado_em || entrada.data_conversa || entrada.dia);
    const diaISO = toDay(entrada.atualizado_em || entrada.data_conversa || entrada.dia);
    return { ...entrada, timestamp, diaISO };
  })
  .filter((entrada) => entrada.diaISO);
const diasComNovas = new Set();
const ordenarPorDia = (a, b) => Date.parse(a[0]) - Date.parse(b[0]);
const diasMap = new Map();
for (const entrada of conversasNormalizadas) {
  const chaveDia = entrada.diaISO.toISOString();
  const registro = diasMap.get(chaveDia) || { timestamp: entrada.timestamp };
  registro.timestamp = Math.max(registro.timestamp || 0, entrada.timestamp || 0);
  diasMap.set(chaveDia, registro);
}
const diasOrdenados = Array.from(diasMap.entries()).sort(ordenarPorDia);
const ultimaRegistradaDia = estado.ultima_conversa_em ? toDay(estado.ultima_conversa_em) : null;
const ultimaRegistradaTimestamp = toTimestamp(estado.ultima_conversa_em);
const isConversaNova = (entrada) => {
  if (!ultimaRegistradaDia) return true;
  const dia = entrada.diaISO;
  if (dia > ultimaRegistradaDia) return true;
  if (dia.getTime() === ultimaRegistradaDia.getTime()) {
    if (!ultimaRegistradaTimestamp) return true;
    return (entrada.timestamp || 0) > ultimaRegistradaTimestamp;
  }
  return false;
};
const XP_POR_DIA = 75;
let xpGanho = 0;
for (const entrada of conversasNormalizadas) {
  if (!isConversaNova(entrada)) continue;
  diasComNovas.add(entrada.diaISO.toISOString());
}
xpGanho += diasComNovas.size * XP_POR_DIA;
let sequenciaAtual = 0;
let sequenciaRecordeCalculado = 0;
let reinicios = Number(status.reinicios) || 0;
let ultimaDataProcessada = null;
let ultimaTimestampProcessado = ultimaRegistradaTimestamp || null;
const applyReset = () => {
  if (sequenciaAtual > 0) {
    reinicios += 1;
  }
  sequenciaAtual = 0;
};
let metaAlcancadaIdxAnterior = metas.findIndex((meta) => meta.codigo === estado.meta_sequencia_codigo);
if (metaAlcancadaIdxAnterior < 0) metaAlcancadaIdxAnterior = -1;
let metaAlcancadaIdx = metaAlcancadaIdxAnterior;
let alvoIdx = Math.max(metaAlcancadaIdxAnterior + 1, 0);
for (const [diaISO, registro] of diasOrdenados) {
  const diaAtual = new Date(diaISO);
  if (ultimaDataProcessada) {
    const diffDias = Math.round((diaAtual - ultimaDataProcessada) / msDia);
    if (diffDias > 1) {
      applyReset();
      metaAlcancadaIdxAnterior = -1;
      metaAlcancadaIdx = -1;
      alvoIdx = 0;
    }
    if (diffDias === 0) {
      ultimaTimestampProcessado = Math.max(ultimaTimestampProcessado || 0, registro.timestamp || 0);
      ultimaDataProcessada = diaAtual;
      continue;
    }
  }
  sequenciaAtual += 1;
  ultimaDataProcessada = diaAtual;
  ultimaTimestampProcessado = registro.timestamp || ultimaTimestampProcessado || diaAtual.getTime();
  if (sequenciaAtual > sequenciaRecordeCalculado) {
    sequenciaRecordeCalculado = sequenciaAtual;
  }
  const diaTemNovas = diasComNovas.has(diaISO);
  while (alvoIdx < metas.length && sequenciaAtual >= metas[alvoIdx].dias) {
    if (diaTemNovas && alvoIdx > metaAlcancadaIdxAnterior) {
      xpGanho += metas[alvoIdx].bonus;
    }
    metaAlcancadaIdx = alvoIdx;
    alvoIdx += 1;
  }
  metaAlcancadaIdxAnterior = metaAlcancadaIdx;
}
const hojeDia = toDay(new Date());
if (ultimaDataProcessada && hojeDia) {
  const diffDiasHoje = Math.round((hojeDia - ultimaDataProcessada) / msDia);
  if (diffDiasHoje > 1) {
    applyReset();
    metaAlcancadaIdxAnterior = -1;
    metaAlcancadaIdx = -1;
    alvoIdx = 0;
  }
}
const sequenciaRecorde = Math.max(sequenciaRecordeCalculado, Number(estado.sequencia_recorde) || 0);
const metaCodigoAtual = metaAlcancadaIdx >= 0 ? metas[metaAlcancadaIdx].codigo : metas[0].codigo;
const proximaMetaAlvo = determineNextMeta(sequenciaAtual);
const proximaCodigo = proximaMetaAlvo ? proximaMetaAlvo.codigo : null;
const alvoConversas = proximaMetaAlvo ? proximaMetaAlvo.dias : metaTargets[metaTargets.length - 1].dias;
const ultimaConversaISO = ultimaTimestampProcessado
  ? new Date(ultimaTimestampProcessado).toISOString()
  : estado.ultima_conversa_em || null;
return [{
  json: {
    usuario_id: estado.usuario_id,
    xp_ganho: xpGanho,
    sequencia_atual: sequenciaAtual,
    sequencia_recorde: sequenciaRecorde,
    meta_sequencia_codigo: metaCodigoAtual,
    proxima_meta_codigo: proximaCodigo,
    ultima_conversa_em: ultimaConversaISO,
    sequencia_status: { ...status, estado: 'ativa', reinicios, alvo_conversas: alvoConversas },
    dias_processados: diasOrdenados.length,
  },
}];

const entrada = $items('Validar Entrada', 0, 0) || [];
const payload = entrada[0]?.json || {};
return [{ json: { usuario_id: payload.usuario_id || null } }];
