const item = $input.item.json || {};

const parseList = (value) => {
  if (!value) return [];
  if (Array.isArray(value)) return value;
  if (typeof value === 'string') {
    try {
      const parsed = JSON.parse(value);
      if (Array.isArray(parsed)) return parsed;
    } catch (error) {}
  }
  return [];
};

const usuarioId = item.usuario_id;
if (!usuarioId) {
  throw new Error('usuario_id obrigatório');
}

const quests = parseList(item.quests_personalizadas);
const updates = parseList(item.atualizacoes_status);

return [{
  json: {
    usuario_id: usuarioId,
    novas_quests: quests,
    atualizacoes: updates
  }
}];

const entradaLista = $items("Normalizar Entrada") || [];
const entrada = (entradaLista[0] && entradaLista[0].json) || {};
const estado = $input.item.json || {};

return [{
  json: {
    usuario_id: entrada.usuario_id,
    novas_quests: Array.isArray(entrada.novas_quests) ? entrada.novas_quests : [],
    atualizacoes: Array.isArray(entrada.atualizacoes) ? entrada.atualizacoes : [],
    estado_inicial: estado
  }
}];

const estadoItems = $items("Atualizar Estado", 0, 0) || [];
const estadoAtualizado = (estadoItems[0] && estadoItems[0].json) || {};
const snapshotFinal = $input.item.json || {};

const parseList = (value) => {
  if (!value) return [];
  if (Array.isArray(value)) return value;
  if (typeof value === 'string') {
    try {
      const parsed = JSON.parse(value);
      return Array.isArray(parsed) ? parsed : [];
    } catch (error) {
      return [];
    }
  }
  return [];
};

const toNumber = (value, fallback = 0) => {
  const numeric = Number(value);
  return Number.isFinite(numeric) ? numeric : fallback;
};

const filaItems = $items("Preparar Operacoes", 0, 0) || [];
const filaEspera = parseList(filaItems[0]?.json?.fila_espera);

return [{
  json: {
    usuario_id: estadoAtualizado.usuario_id ?? snapshotFinal.usuario_id ?? null,
    xp_total: toNumber(estadoAtualizado.xp_total ?? snapshotFinal.xp_total ?? 0),
    nivel_atual: estadoAtualizado.nivel_atual ?? snapshotFinal.nivel_atual ?? null,
    titulo_nivel: estadoAtualizado.titulo_nivel ?? snapshotFinal.titulo_nivel ?? null,
    xp_proximo_nivel: toNumber(estadoAtualizado.xp_proximo_nivel ?? snapshotFinal.xp_proximo_nivel ?? 0),
    xp_ganho: toNumber(estadoAtualizado.xp_ganho, 0),
    quests_concluidas_no_evento: toNumber(estadoAtualizado.quests_concluidas, 0),
    reinicios_registrados: toNumber(estadoAtualizado.reinicios, 0),
    novas_quests_registradas: parseList(estadoAtualizado.novas_quests),
    atualizacoes_processadas: parseList(estadoAtualizado.atualizacoes),
    fila_espera: filaEspera,
    snapshot_final: snapshotFinal
  }
}];
