// No n8n webhook POST com Content-Type: application/json, o body vem parseado em input.json.body
const input = $input.first().json;

// Body parseado automaticamente pelo n8n
const body = input.body || {};
const query = input.query || {};

// Buscar valores (prioridade: body > query)
const tipoRaw = body.tipo || query.tipo || null;
const idRaw = body.id || query.id || null;
const anotacaoRaw = body.anotacao !== undefined ? body.anotacao : (query.anotacao !== undefined ? query.anotacao : null);
const usuarioIdRaw = body.usuario_id || body.user_id || query.usuario_id || query.user_id || null;

const tipo = tipoRaw ? String(tipoRaw).toLowerCase().trim() : '';
const id = idRaw ? String(idRaw).trim() : null;
const anotacao = anotacaoRaw !== null && anotacaoRaw !== undefined ? String(anotacaoRaw).trim() : null;
const usuarioId = usuarioIdRaw ? String(usuarioIdRaw).trim() : null;

if (!tipo || !['conversa', 'quest'].includes(tipo)) {
  throw new Error('tipo deve ser "conversa" ou "quest". Recebido: ' + JSON.stringify({ tipo: tipo, body: body, query: query }));
}

if (!id) {
  throw new Error('id é obrigatório. Recebido: ' + JSON.stringify({ id: id, body: body, query: query }));
}

if (!usuarioId) {
  throw new Error('usuario_id é obrigatório. Recebido: ' + JSON.stringify({ usuario_id: usuarioId, body: body, query: query }));
}

return [{
  json: {
    tipo: tipo,
    id: id,
    anotacao: anotacao,
    usuario_id: usuarioId
  }
}];

// O nó recebe input de ambos nós Postgres (conversa e quest)
// Como ambos conectam aqui, ele executa 2 vezes
// Usar $input.first() que terá o resultado do nó que foi executado
const resultado = $input.first()?.json;
const validacao = $('Validar Entrada').first()?.json;

// Se não tem id no resultado, provavelmente veio do nó que não atualizou nada
// Retornar resposta vazia que será ignorada (o primeiro nó já respondeu)
if (!resultado || !resultado.id) {
  return [{ json: { _skip: true } }];
}

const tipo = validacao?.tipo || 'quest';
const id = validacao?.id || resultado.id;

return [{
  json: {
    success: true,
    tipo: tipo,
    id: id,
    anotacao: resultado.anotacoes_usr || resultado.anotacoes_quest || null,
    atualizado_em: resultado.atualizado_em || new Date().toISOString()
  }
}];

const query = $input.first().json.query || {};
const tipo = (query.tipo || '').toLowerCase().trim();
const id = query.id || query.conversa_id || query.quest_id || null;
const user_id = query.user_id || query.usuario_id || null;

if (!tipo || !['conversa', 'quest'].includes(tipo)) {
  throw new Error('tipo deve ser "conversa" ou "quest"');
}

if (!id) {
  throw new Error('id é obrigatório');
}

if (!user_id) {
  throw new Error('user_id é obrigatório');
}

return [{ json: { tipo, id, user_id } }];

const resultado = $input.first()?.json;
const validacao = $('Validar Params GET').first()?.json;

return [{
  json: {
    success: true,
    tipo: validacao?.tipo || 'conversa',
    id: validacao?.id || resultado?.id,
    anotacao: resultado?.anotacao || null,
    atualizado_em: resultado?.atualizado_em || null
  }
}];
