const userId = $json.query?.user_id ?? $json.body?.user_id;

if (typeof userId !== 'string' || userId.trim() === '') {
  throw new Error('Parâmetro user_id é obrigatório (?user_id=).');
}

return [
  {
    json: {
      user_id: userId.trim(),
    },
  },
];

const rows = $input.all().map(item => item.json || {});
const find = (fn) => rows.find(fn) || {};
const sequencia = find(row => row.bloco === 'sequencia');

const metaAlvo = Number(sequencia.meta_alvo_dias ?? 3) || 3;
const streakAtual = Number(sequencia.sequencia_atual ?? 0) || 0;
const progressoAtual = Math.min(streakAtual, metaAlvo);
const percentual = metaAlvo > 0 ? Math.round((progressoAtual / metaAlvo) * 100) : 0;

const proximaMetaDias = Number(sequencia.proxima_meta_dias ?? (metaAlvo >= 3 ? metaAlvo + 2 : 5)) || 5;
const xpBase = Number(sequencia.xp_base_diaria ?? 75) || 0;
const xpBonus = Number(sequencia.xp_bonus_proxima_meta ?? sequencia.xp_bonus_meta ?? 0) || 0;

const ultimaConversacaoIso = sequencia.ultima_conversa_em || null;

function humanizeDiff(iso) {
  if (!iso) return null;
  const date = new Date(iso);
  if (Number.isNaN(date.getTime())) return null;
  const now = new Date();
  let diffMs = Math.max(0, now.getTime() - date.getTime());
  const days = Math.floor(diffMs / 86_400_000);
  diffMs -= days * 86_400_000;
  const hours = Math.floor(diffMs / 3_600_000);
  const parts = [];
  if (days > 0) parts.push(`${days} dia${days > 1 ? 's' : ''}`);
  if (hours > 0) parts.push(`${hours} h`);
  if (parts.length === 0) parts.push('menos de 1h');
  return `há ${parts.join(' e ')}`;
}

const beneficios = [];
if (xpBase > 0) beneficios.push(`+${xpBase} XP base`);
if (xpBonus > 0) beneficios.push(`+${xpBonus} XP bônus`);
beneficios.push('Novo insight personalizado');
beneficios.push('Progresso na Quest ativa');

const response = {
  success: true,
  usuario_id: sequencia.user_id || null,
  card_conversas: {
    streak: {
      atual: streakAtual,
      recorde: Number(sequencia.sequencia_recorde ?? streakAtual) || 0,
      meta_codigo: sequencia.meta_sequencia_codigo || 'streak_003',
      proxima_meta_codigo: sequencia.proxima_meta_codigo || 'streak_005'
    },
    progresso: {
      atual: progressoAtual,
      meta: metaAlvo,
      percentual,
      label: `${progressoAtual}/${metaAlvo}`
    },
    ultima_conversa: {
      timestamp: ultimaConversacaoIso,
      label: humanizeDiff(ultimaConversacaoIso) || 'Sem registros recentes'
    },
    xp: {
      base: xpBase,
      bonus_proxima_meta: xpBonus,
      proxima_meta_dias: proximaMetaDias,
      proxima_meta_titulo: sequencia.meta_prox_titulo || null
    },
    beneficios
  }
};

return [
  {
    json: response,
  },
];
