const userId = $json.query?.user_id ?? $json.body?.user_id;

if (typeof userId !== 'string' || userId.trim() === '') {
  throw new Error('Parâmetro user_id é obrigatório (?user_id=).');
}

return [
  {
    json: {
      user_id: userId.trim(),
    },
  },
];

const rows = $input.all().map(item => item.json || {});

function findRow(bloco) {
  return rows.find((row) => row.bloco === bloco) || null;
}

const humorRow = findRow('humor');
const panasRow = findRow('panas');
const sabotadorRow = findRow('sabotador');
const emocaoRows = rows.filter((row) => row.bloco === 'emocoes');

const humorMedia = humorRow?.humor_medio_7d ?? null;
const humorAtual = humorRow?.humor_atual ?? humorMedia;

const response = {
  success: true,
  usuario_id: humorRow?.user_id || panasRow?.user_id || sabotadorRow?.user_id || emocaoRows[0]?.user_id || null,
  card_panorama_emocional: {
    humor: {
      media_7d: humorMedia,
      atual: humorAtual,
      amostras: humorRow?.total_registros ?? 0,
      ultima_data: humorRow?.ultima_data ?? null,
      ultima_hora: humorRow?.ultima_hora ?? null,
    },
    energia: {
      percentual_positiva: panasRow?.percentual_positivas ?? 0,
      percentual_negativa: panasRow?.percentual_negativas ?? 0,
      percentual_neutra: panasRow?.percentual_neutras ?? 0,
      categoria: panasRow?.resultado_panas ?? 'NEUTRA',
    },
    emocoes_dominantes: emocaoRows.map((row) => ({
      nome: row.emocao_nome ?? 'Emoção',
      emoji: row.emoji ?? '❤️',
      percentual: row.percentual ?? 0,
    })),
    sabotador: sabotadorRow
      ? {
          id: sabotadorRow.sabotador_id,
          nome: sabotadorRow.nome,
          emoji: sabotadorRow.emoji,
          apelido: sabotadorRow.apelido_personalizado,
          contexto: sabotadorRow.contexto_principal,
          insight: sabotadorRow.insight_atual,
          contramedida: sabotadorRow.contramedida_ativa,
          intensidade_media: sabotadorRow.intensidade_media,
          total_deteccoes: sabotadorRow.total_deteccoes,
          conversas_afetadas: sabotadorRow.conversas_afetadas,
        }
      : null,
  },
};

return [
  {
    json: response,
  },
];
