const userId = $json.query?.user_id ?? $json.body?.user_id;

if (typeof userId !== 'string' || userId.trim() === '') {
  throw new Error('Parâmetro user_id é obrigatório (?user_id=).');
}

return [{ json: { user_id: userId.trim() } }];

const row = $input.first()?.json || {};

const meta = Number(row.progresso_meta ?? 1) || 1;
const atual = Math.min(Number(row.progresso_atual ?? 0) || 0, meta);
const percentual = meta > 0 ? Math.round((atual / meta) * 100) : 0;

const beneficios = [];
const xpBase = Number(row.xp_base_default ?? 150) || 150;
const xpBonus = Number(row.xp_bonus_recorrencia ?? 30) || 0;

beneficios.push(`+${xpBase} XP base`);
if (xpBonus > 0) beneficios.push(`+${xpBonus} XP bônus (recorrência)`);
beneficios.push('Novo insight aplicado na prática');
beneficios.push('Progresso em hábitos chaves');

function humanizeDate(iso) {
  if (!iso) return null;
  const date = new Date(iso);
  if (Number.isNaN(date.getTime())) return null;
  const now = new Date();
  const diffMs = Math.max(0, now.getTime() - date.getTime());
  const days = Math.floor(diffMs / 86_400_000);
  const hours = Math.floor((diffMs % 86_400_000) / 3_600_000);
  const parts = [];
  if (days > 0) parts.push(`${days} dia${days > 1 ? 's' : ''}`);
  if (hours > 0) parts.push(`${hours} h`);
  if (parts.length === 0) return 'agora';
  return `há ${parts.join(' e ')}`;
}

const quest = row.quest_id
  ? {
      id: row.quest_id,
      titulo: row.titulo ?? 'Quest personalizada',
      descricao: row.descricao ?? 'Continue avançando na sua jornada.',
      status: row.status ?? 'pendente',
      prioridade: row.prioridade ?? null,
      recorrencia: row.recorrencia ?? null,
      progresso: {
        atual,
        meta,
        percentual,
        label: `${atual}/${meta}`,
      },
      xp_recompensa: Number(row.xp_calculado ?? xpBase) || xpBase,
      ultima_atualizacao: row.atualizado_em ?? null,
      ultima_atualizacao_label: humanizeDate(row.atualizado_em),
    }
  : null;

const response = {
  success: true,
  usuario_id: row.user_id ?? null,
  card_quests: {
    quest,
    snapshot: {
      total_concluidas: Number(row.total_quests_concluidas ?? 0) || 0,
      total_personalizadas: Number(row.total_quests_personalizadas ?? 0) || 0,
      xp_base_total: Number(row.xp_base_sum ?? 0) || 0,
      xp_bonus_total: Number(row.xp_bonus_sum ?? 0) || 0,
    },
    beneficios,
    recompensas: {
      xp_base: xpBase,
      xp_bonus_recorrencia: xpBonus,
    },
  },
};

return [{ json: response }];
