const rows = $input.all().map(item => item.json);

const objetivosMap = new Map();

for (const row of rows) {
  if (!row.objetivo_id) continue;

  if (!objetivosMap.has(row.objetivo_id)) {
    objetivosMap.set(row.objetivo_id, {
      id: row.objetivo_id,
      titulo: row.objetivo_titulo,
      is_padrao: row.is_padrao,
      area: {
        nome: row.area_nome,
        icone: row.area_icone
      },
      quests: []
    });
  }

  if (row.quest_id) {
    objetivosMap.get(row.objetivo_id).quests.push({
      id: row.quest_id,
      titulo: row.quest_titulo,
      tipo_impacto: row.tipo_impacto,
      concluidas: row.concluidas,
      total: row.total,
      percentual: row.percentual
    });
  }
}

const objetivos = Array.from(objetivosMap.values());

let totalQuests = 0;
let totalConcluidas = 0;
let totalRecorrencias = 0;

for (const obj of objetivos) {
  totalQuests += obj.quests.length;
  for (const q of obj.quests) {
    totalConcluidas += q.concluidas;
    totalRecorrencias += q.total;
  }
}

return [{
  json: {
    success: true,
    objetivos: objetivos,
    estatisticas: {
      total_objetivos: objetivos.length,
      total_quests: totalQuests,
      total_concluidas: totalConcluidas,
      total_recorrencias: totalRecorrencias,
      percentual_geral: totalRecorrencias > 0 
        ? Math.round((totalConcluidas / totalRecorrencias) * 100) 
        : 0
    }
  }
}];
