const rows = $input.all().map(item => item.json);

const sabotadoresMap = new Map();

for (const row of rows) {
  if (!row.sabotador_id) continue;

  if (!sabotadoresMap.has(row.sabotador_id)) {
    sabotadoresMap.set(row.sabotador_id, {
      id: row.sabotador_id,
      nome: row.sabotador_nome,
      emoji: row.sabotador_emoji || 'ðŸ§ ',
      quests: []
    });
  }

  if (row.quest_id) {
    sabotadoresMap.get(row.sabotador_id).quests.push({
      id: row.quest_id,
      titulo: row.quest_titulo,
      concluidas: Number(row.concluidas) || 0,
      total: Number(row.total) || 0,
      percentual: Number(row.percentual) || 0
    });
  }
}

const sabotadores = Array.from(sabotadoresMap.values());

// Ordenar sabotadores pelo nÃºmero de aÃ§Ãµes (quests) - do maior para o menor
sabotadores.sort((a, b) => b.quests.length - a.quests.length);

let totalQuests = 0;
let totalConcluidas = 0;
let totalRecorrencias = 0;

for (const sab of sabotadores) {
  totalQuests += sab.quests.length;
  for (const q of sab.quests) {
    totalConcluidas += q.concluidas;
    totalRecorrencias += q.total;
  }
}

return [{
  json: {
    success: true,
    sabotadores: sabotadores,
    estatisticas: {
      total_sabotadores: sabotadores.length,
      total_quests: totalQuests,
      total_concluidas: totalConcluidas,
      total_recorrencias: totalRecorrencias,
      percentual_geral: totalRecorrencias > 0 
        ? Math.round((totalConcluidas / totalRecorrencias) * 100) 
        : 0
    }
  }
}];

