const query = $input.first().json.query || {};
const user_id = query.user_id || query.userId || null;

if (!user_id) {
  throw new Error('user_id obrigatório');
}

return [{ json: { user_id: user_id } }];

const item = $input.first()?.json || {};

// Se não encontrou, retornar valores padrão
if (!item || !item.usuario_id) {
  return [{
    json: {
      success: true,
      notificacoes: {
        conversas_ia_ativo: true,
        conversas_ia_periodo: 'manha',
        conversas_ia_motivacionais: true,
        conversas_ia_sabotadores: true,
        conversas_ia_resumo_semanal: true,
        lembretes_ativo: true,
        lembretes_periodo: 'manha',
        lembretes_conversas_diarias: true,
        lembretes_quests: true,
        lembretes_conquistas: true,
        lembretes_canais: ['whatsapp']
      }
    }
  }];
}

return [{
  json: {
    success: true,
    notificacoes: {
      conversas_ia_ativo: item.conversas_ia_ativo ?? true,
      conversas_ia_periodo: item.conversas_ia_periodo || 'manha',
      conversas_ia_motivacionais: item.conversas_ia_motivacionais ?? true,
      conversas_ia_sabotadores: item.conversas_ia_sabotadores ?? true,
      conversas_ia_resumo_semanal: item.conversas_ia_resumo_semanal ?? true,
      lembretes_ativo: item.lembretes_ativo ?? true,
      lembretes_periodo: item.lembretes_periodo || 'manha',
      lembretes_conversas_diarias: item.lembretes_conversas_diarias ?? true,
      lembretes_quests: item.lembretes_quests ?? true,
      lembretes_conquistas: item.lembretes_conquistas ?? true,
      lembretes_canais: item.lembretes_canais || ['whatsapp']
    }
  }
}];

const body = $input.first().json.body || {};
const user_id = body.user_id || body.usuario_id || null;

if (!user_id) {
  throw new Error('user_id obrigatório');
}

// Validar periodo
const periodosValidos = ['manha', 'tarde', 'noite'];
const conversas_ia_periodo = periodosValidos.includes(body.conversas_ia_periodo) ? body.conversas_ia_periodo : 'manha';
const lembretes_periodo = periodosValidos.includes(body.lembretes_periodo) ? body.lembretes_periodo : 'manha';

const dados = {
  user_id: user_id,
  conversas_ia_ativo: body.conversas_ia_ativo ?? true,
  conversas_ia_periodo: conversas_ia_periodo,
  conversas_ia_motivacionais: body.conversas_ia_motivacionais ?? true,
  conversas_ia_sabotadores: body.conversas_ia_sabotadores ?? true,
  conversas_ia_resumo_semanal: body.conversas_ia_resumo_semanal ?? true,
  lembretes_ativo: body.lembretes_ativo ?? true,
  lembretes_periodo: lembretes_periodo,
  lembretes_conversas_diarias: body.lembretes_conversas_diarias ?? true,
  lembretes_quests: body.lembretes_quests ?? true,
  lembretes_conquistas: body.lembretes_conquistas ?? true,
  lembretes_canais: Array.isArray(body.lembretes_canais) ? body.lembretes_canais : ['whatsapp']
};

return [{ json: dados }];

const item = $input.first()?.json || {};

if (!item || !item.usuario_id) {
  return [{
    json: {
      success: false,
      error: 'Erro ao salvar notificações'
    }
  }];
}

return [{
  json: {
    success: true,
    notificacoes: {
      conversas_ia_ativo: item.conversas_ia_ativo ?? true,
      conversas_ia_periodo: item.conversas_ia_periodo || 'manha',
      conversas_ia_motivacionais: item.conversas_ia_motivacionais ?? true,
      conversas_ia_sabotadores: item.conversas_ia_sabotadores ?? true,
      conversas_ia_resumo_semanal: item.conversas_ia_resumo_semanal ?? true,
      lembretes_ativo: item.lembretes_ativo ?? true,
      lembretes_periodo: item.lembretes_periodo || 'manha',
      lembretes_conversas_diarias: item.lembretes_conversas_diarias ?? true,
      lembretes_quests: item.lembretes_quests ?? true,
      lembretes_conquistas: item.lembretes_conquistas ?? true,
      lembretes_canais: item.lembretes_canais || ['whatsapp']
    }
  }
}];
