const webhookData = $input.first().json || {};
const query = webhookData.query || {};
const user_id = query.user_id || query.userId || null;

if (!user_id) {
  throw new Error('user_id obrigatório');
}

return [{ json: { user_id: user_id } }];

const items = $input.all().map(i => i.json);

// Se não houver objetivos, retorna lista vazia
if (!items || items.length === 0 || !items[0].id) {
  return [{
    json: {
      success: true,
      objetivos: [],
      total_ativos: 0,
      limite_ativos: 3,
      pode_criar: true
    }
  }];
}

// Formatar objetivos
const objetivos = items.map(obj => ({
  id: obj.id,
  area: {
    id: obj.area_vida_id,
    codigo: obj.area_codigo,
    nome: obj.area_nome,
    icone: obj.area_icone
  },
  objetivo_catalogo_id: obj.objetivo_catalogo_id,
  objetivo_codigo: obj.objetivo_codigo,
  titulo: obj.titulo,
  detalhamento: obj.detalhamento,
  prazo_dias: obj.prazo_dias,
  data_inicio: obj.data_inicio,
  data_limite: obj.data_limite,
  dias_restantes: obj.dias_restantes,
  status: obj.status,
  alcancado_em: obj.alcancado_em,
  criado_em: obj.criado_em,
  is_padrao: obj.is_padrao || false
}));

// Contar todos os ativos (incluindo padrão)
const todosAtivos = objetivos.filter(o => o.status === 'ativo');
// Contar específicos para verificar se pode criar
const especificosAtivos = todosAtivos.filter(o => !o.is_padrao);

return [{
  json: {
    success: true,
    objetivos: objetivos,
    total_ativos: todosAtivos.length,
    limite_ativos: 3,
    pode_criar: especificosAtivos.length < 2
  }
}];

const body = $input.first().json.body || {};
const user_id = body.user_id || body.usuario_id || null;

if (!user_id) {
  throw new Error('user_id obrigatório');
}

// Validações obrigatórias
const area_vida_id = body.area_vida_id || null;
if (!area_vida_id) {
  throw new Error('area_vida_id obrigatório');
}

const titulo = (body.titulo || '').trim();
if (!titulo) {
  throw new Error('titulo obrigatório');
}

const detalhamento = (body.detalhamento || '').trim();
if (!detalhamento || detalhamento.length < 20) {
  throw new Error('detalhamento obrigatório (mínimo 20 caracteres)');
}

const prazo_dias = parseInt(body.prazo_dias, 10);
if (![30, 45, 60].includes(prazo_dias)) {
  throw new Error('prazo_dias deve ser 30, 45 ou 60');
}

// Campos opcionais
const objetivo_catalogo_id = body.objetivo_catalogo_id || null;

const dados = {
  user_id: user_id,
  area_vida_id: area_vida_id,
  objetivo_catalogo_id: objetivo_catalogo_id,
  titulo: titulo,
  detalhamento: detalhamento,
  prazo_dias: prazo_dias
};

return [{ json: dados }];

const item = $input.first()?.json || {};

if (!item || !item.id) {
  return [{
    json: {
      success: false,
      error: 'Erro ao criar objetivo'
    }
  }];
}

return [{
  json: {
    success: true,
    objetivo: {
      id: item.id,
      area_vida_id: item.area_vida_id,
      objetivo_catalogo_id: item.objetivo_catalogo_id,
      titulo: item.titulo,
      detalhamento: item.detalhamento,
      prazo_dias: item.prazo_dias,
      data_inicio: item.data_inicio,
      data_limite: item.data_limite,
      status: item.status,
      criado_em: item.criado_em
    }
  }
}];
