const userId = $json.query?.user_id ?? $json.body?.user_id;
const questId = $json.query?.quest_id ?? $json.body?.quest_id;

if (typeof userId !== 'string' || userId.trim() === '') {
  throw new Error('Parâmetro user_id é obrigatório.');
}

if (typeof questId !== 'string' || questId.trim() === '') {
  throw new Error('Parâmetro quest_id é obrigatório.');
}

return [{ json: { user_id: userId.trim(), quest_id: questId.trim() } }];

const row = $input.first()?.json || {};

if (!row.id) {
  throw new Error('Quest não encontrada');
}

// Calcular estágio baseado no status
let questEstagio = 'a_fazer';
if (row.status === 'disponivel') {
  questEstagio = 'a_fazer';
} else if (row.status === 'ativa') {
  questEstagio = 'fazendo';
} else if (row.status === 'inativa') {
  questEstagio = 'feito';
}

return [{
  json: {
    success: true,
    quest_detail: {
      id: row.id,
      usuario_id: row.usuario_id,
      status: row.status,
      titulo: row.titulo || 'Quest sem título',
      descricao: row.descricao || '',
      xp_recompensa: row.xp_recompensa ? parseInt(row.xp_recompensa) : (row.catalogo_xp ? parseInt(row.catalogo_xp) : null),
      prioridade: row.prioridade || 'media',
      tipo: row.tipo || null,
      complexidade: 0,
      progresso_meta: null,
      progresso_atual: 0,
      xp_concedido: 0,
      recorrencias: row.recorrencias || null,
      quest_estagio: questEstagio,
      config: {
        base_cientifica: row.config_base_cientifica || null
      },
      area_vida: row.area_vida_id ? {
        id: row.area_vida_id,
        nome: row.area_vida_nome,
        descricao: row.area_vida_descricao,
        codigo: row.area_vida_codigo
      } : null,
      sabotador: row.sabotador_id ? {
        id: row.sabotador_id,
        nome: row.sabotador_nome,
        descricao: row.sabotador_descricao,
        contextos_tipicos: row.sabotador_contextos || [],
        contramedidas_sugeridas: row.sabotador_contramedidas || []
      } : null,
      catalogo: row.catalogo_codigo ? {
        codigo: row.catalogo_codigo,
        base_cientifica: row.catalogo_base_cientifica,
        instrucoes: row.catalogo_instrucoes,
        tempo_estimado_min: row.catalogo_tempo_estimado,
        dificuldade: row.catalogo_dificuldade,
        categoria: row.catalogo_categoria
      } : null
    }
  }
}];
