const input = $input.first()?.json ?? {};
const query = input.query ?? {};
const body = input.body ?? {};

const candidate = [
  query.usuario_id,
  query.user_id,
  query.id_usuario,
  body.usuario_id,
  body.user_id,
  body.id_usuario,
  input.headers?.usuario_id,
  input.headers?.user_id,
].find((value) => typeof value === 'string' && value.trim().length > 0);

if (!candidate) {
  throw new Error('usuario_id obrigatÃ³rio');
}

return [{
  json: {
    usuario_id: candidate.trim(),
  }
}];

const getItem = (nodeName) => {
  const list = $items(nodeName, 0, 0) || [];
  return list[0]?.json || {};
};

const getItems = (nodeName) => {
  const list = $items(nodeName, 0, 0) || [];
  return list.map((entry) => entry?.json || {});
};

const toNumber = (value, fallback = null) => {
  if (value === null || value === undefined || value === '') return fallback;
  const coerced = Number(value);
  return Number.isFinite(coerced) ? coerced : fallback;
};

const parseJson = (value) => {
  if (!value) return null;
  if (typeof value === 'object') return value;
  if (typeof value !== 'string') return null;
  const trimmed = value.trim();
  if (!trimmed) return null;
  try {
    return JSON.parse(trimmed);
  } catch (_err) {
    return null;
  }
};

const sequenciaStatus = parseJson(getItem('Buscar Estado').sequencia_status) || getItem('Buscar Estado').sequencia_status || null;
const estado = getItem('Buscar Estado');
const questsRaw = getItems('Buscar Quests');
const historicoConversasRaw = getItems('Conversas Recentes');

const quests = questsRaw
  .map((row) => {
    if (!row) return null;
    const config = parseJson(row.config);
    return {
      instancia_id: row.instancia_id ?? null,
      meta_codigo: row.meta_codigo ?? '',
      status: row.status ?? 'pendente',
      titulo: row.titulo ?? 'Quest personalizada',
      descricao: row.descricao ?? null,
      contexto_origem: row.contexto_origem ?? null,
      progresso_meta: toNumber(row.progresso_meta, 1) ?? 1,
      progresso_atual: toNumber(row.progresso_atual, 0) ?? 0,
      concluido_em: row.concluido_em ?? null,
      config: config ?? null,
      xp_recompensa: toNumber(row.xp_recompensa, config?.xp_recompensa ?? null),
      prioridade: row.prioridade ?? config?.prioridade ?? null,
      recorrencia: row.recorrencia ?? config?.recorrencia ?? null,
    };
  })
  .filter((item) => item !== null);

const historicoConversas = historicoConversasRaw.map((row) => ({
  dia: row.dia,
  conversou: Boolean(row.conversou),
}));

return [{
  json: {
    usuario_id: estado.usuario_id ?? $json.usuario_id ?? null,
    xp_total: toNumber(estado.xp_total, 0) ?? 0,
    xp_proximo_nivel: toNumber(estado.xp_proximo_nivel, null),
    nivel_atual: toNumber(estado.nivel_atual, 1) ?? 1,
    titulo_nivel: estado.titulo_nivel ?? null,
    sequencia_atual: toNumber(estado.sequencia_atual, 0) ?? 0,
    sequencia_recorde: toNumber(estado.sequencia_recorde, 0) ?? 0,
    meta_sequencia_codigo: estado.meta_sequencia_codigo ?? null,
    proxima_meta_codigo: estado.proxima_meta_codigo ?? null,
    sequencia_status: sequenciaStatus,
    historico_conversas: historicoConversas,
    quests_personalizadas: quests,
  },
}];
